};

const Inventory = ({ data }) => {
  const getStockStatus = (qty, safe) => {
    if (qty <= 0) return { label: '缂鸿波', color: 'bg-red-100 text-red-700' };
    if (qty <= safe) return { label: '搴瓨鍋忎綆', color: 'bg-orange-100 text-orange-700' };
