              <th className="px-6 py-4">瑕忔牸 / 鍨嬭櫉</th>
              <th className="px-6 py-4">搴瓨鏁搁噺</th>
              <th className="px-6 py-4">鐙€鎱?/th>
              <th className="px-6 py-4">瀛樻斁浣嶇疆</th>
              <th className="px-6 py-4">鍌欒ɑ</th>
            </tr>
          </thead>
          <tbody className="divide-y divide-gray-100">
            {data.inventory.map(item => {
              const status = getStockStatus(item.quantity, item.safeStock);
              return (
                <tr key={item.id} className="hover:bg-gray-50/50">
                  <td className="px-6 py-4">
                    <div className="flex items-center gap-3">
                      <div className="p-2 bg-gray-100 rounded text-gray-500">
                        <Package size={18} />
                      </div>
                      <span className="font-medium text-gray-900">{item.name}</span>
                    </div>
                  </td>
                  <td className="px-6 py-4">
                    <span className="bg-gray-100 text-gray-600 px-2 py-1 rounded text-xs font-mono">
                      {item.spec}
                    </span>
                  </td>
                  <td className="px-6 py-4 text-gray-900 font-medium">
                    {item.quantity} <span className="text-gray-400 text-xs font-normal">{item.unit}</span>
                  </td>
                  <td className="px-6 py-4">
                    <span className={`px-2 py-1 rounded-full text-xs font-medium ${status.color}`}>
                      {status.label}
                    </span>
                  </td>
                  <td className="px-6 py-4 text-gray-600 font-mono">
                    {item.location}
                  </td>
                  <td className="px-6 py-4 text-gray-400">
                    -
                  </td>
                </tr>
              )
            })}
          </tbody>
        </table>
      </Card>
    </div>
  );
};

// --- PLACEHOLDER PAGES ---

const PlaceholderPage = ({ title, icon: Icon }) => (
