鏁村悎瑷畾" icon={Link2} />;
const Storage = () => <PlaceholderPage title="鏂囦欢绠＄悊" icon={FolderOpen} />;

// --- MAIN LAYOUT & APP ---

const SidebarGroup = ({ label, children }) => (
  <div className="mb-4">
    <div className="px-4 py-2 text-xs font-semibold text-gray-400 uppercase tracking-wider">{label}</div>
