import React, { useState, useMemo } from 'react';
import { Modal } from '../common/Modal';
import { Calendar, FileSpreadsheet, Search, Download, Filter, X } from 'lucide-react';

// 鏃ユ湡鍗€闁撻伕闋匼r
const DATE_RANGE_OPTIONS = [
    { id: 'today', label: '浠婃棩' },
    { id: 'week', label: '鏈€? },
    { id: 'month', label: '鏈湀' },
    { id: 'custom', label: '鑷▊' }
];

// 鏃ユ湡鍗€闁撹▓绠梊r
const getDateRange = (rangeType, customStart, customEnd) => {
    const today = new Date();
    today.setHours(0, 0, 0, 0);

    switch (rangeType) {
        case 'today':
            return {
                startDate: today.toISOString().slice(0, 10),
                endDate: today.toISOString().slice(0, 10)
            };
        case 'week':
            const weekStart = new Date(today);
            weekStart.setDate(today.getDate() - today.getDay());
            return {
                startDate: weekStart.toISOString().slice(0, 10),
                endDate: today.toISOString().slice(0, 10)
            };
        case 'month':
            const monthStart = new Date(today.getFullYear(), today.getMonth(), 1);
            return {
                startDate: monthStart.toISOString().slice(0, 10),
                endDate: today.toISOString().slice(0, 10)
            };
        case 'custom':
            return { startDate: customStart, endDate: customEnd };
        default:
            return { startDate: '', endDate: '' };
    }
};

export const FinanceExportModal = ({
    isOpen,
    onClose,
    transactions = [],
    accounts = [],
    projects = [],
    onExport
}) => {
    const [rangeType, setRangeType] = useState('month');
    const [customStartDate, setCustomStartDate] = useState('');
    const [customEndDate, setCustomEndDate] = useState('');
    const [isExporting, setIsExporting] = useState(false);

    // 瑷堢畻鏃ユ湡绡勫湇
    const dateRange = useMemo(() => {
        return getDateRange(rangeType, customStartDate, customEndDate);
    }, [rangeType, customStartDate, customEndDate]);

    // 寤虹珛甯虫埗鍜屽皥妗堢殑鏌ヨ鏄犲皠
    const accountsMap = useMemo(() => {
        const map = {};
        accounts.forEach(acc => { map[acc.id] = acc.name; });
        return map;
    }, [accounts]);

    const projectsMap = useMemo(() => {
        const map = {};
        projects.forEach(p => { map[p.id] = p.name; });
        return map;
    }, [projects]);

    // 绡╅伕浜ゆ槗
    const filteredTransactions = useMemo(() => {
        if (!dateRange.startDate || !dateRange.endDate) return transactions;

        return transactions.filter(tx => {
            const txDate = tx.date;
            return txDate >= dateRange.startDate && txDate <= dateRange.endDate;
        });
    }, [transactions, dateRange]);

    // 瑷堢畻绲辫▓
    const stats = useMemo(() => {
        let income = 0, expense = 0;
        filteredTransactions.forEach(tx => {
            if (tx.type === '鏀跺叆') income += tx.amount;
            else expense += tx.amount;
        });
        return { income, expense, net: income - expense, count: filteredTransactions.length };
    }, [filteredTransactions]);

    // 铏曠悊鍖嚭
    const handleExport = async () => {
        setIsExporting(true);
        try {
            await onExport(filteredTransactions, {
                dateRange,
                accountsMap,
                projectsMap
            });
            onClose();
        } catch (error) {
            console.error('Export failed:', error);
        } finally {
            setIsExporting(false);
        }
    };

    return (
        <Modal
            isOpen={isOpen}
            onClose={onClose}
            title="馃搳 鍖嚭璨″嫏鍫辫〃"
            onConfirm={handleExport}
            confirmDisabled={isExporting || filteredTransactions.length === 0}
            confirmText={isExporting ? '鍖嚭涓?..' : '鍖嚭鍒?Google Sheet'}
        >
            <div className="space-y-5">
                {/* 鏃ユ湡鍗€闁撻伕鎿?*/}
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">
                        <Calendar size={14} className="inline mr-1" />
                        鏃ユ湡鍗€闁揬r
                    </label>
                    <div className="flex gap-2 flex-wrap">
                        {DATE_RANGE_OPTIONS.map(option => (
                            <button
                                key={option.id}
                                onClick={() => setRangeType(option.id)}
                                className={`px-4 py-2 rounded-lg text-sm font-medium transition-all ${rangeType === option.id
                                        ? 'bg-blue-600 text-white'
                                        : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                                    }`}
                            >
                                {option.label}
                            </button>
                        ))}
                    </div>
                </div>

                {/* 鑷▊鏃ユ湡杓稿叆 */}
                {rangeType === 'custom' && (
                    <div className="grid grid-cols-2 gap-4 p-4 bg-gray-50 rounded-lg">
                        <div>
                            <label className="block text-xs text-gray-500 mb-1">璧峰鏃ユ湡</label>
                            <input
                                type="date"
                                value={customStartDate}
                                onChange={(e) => setCustomStartDate(e.target.value)}
                                className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-200 focus:border-blue-500 outline-none"
                            />
                        </div>
                        <div>
                            <label className="block text-xs text-gray-500 mb-1">绲愭潫鏃ユ湡</label>
                            <input
                                type="date"
                                value={customEndDate}
                                onChange={(e) => setCustomEndDate(e.target.value)}
                                className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-200 focus:border-blue-500 outline-none"
                            />
                        </div>
                    </div>
                )}

                {/* 闋愯绲辫▓ */}
                <div className="p-4 bg-gradient-to-r from-blue-50 to-indigo-50 rounded-xl border border-blue-100">
                    <div className="text-sm font-medium text-gray-700 mb-3">鍖嚭闋愯</div>
                    <div className="grid grid-cols-4 gap-3 text-center">
                        <div>
                            <div className="text-xl font-bold text-gray-800">{stats.count}</div>
                            <div className="text-xs text-gray-500">绛嗕氦鏄?/div>
                        </div>
                        <div>
                            <div className="text-xl font-bold text-green-600">
                                ${stats.income.toLocaleString()}
                            </div>
                            <div className="text-xs text-gray-500">绺芥敹鍏?/div>
                        </div>
                        <div>
                            <div className="text-xl font-bold text-red-500">
                                ${stats.expense.toLocaleString()}
                            </div>
                            <div className="text-xs text-gray-500">绺芥敮鍑?/div>
                        </div>
                        <div>
                            <div className={`text-xl font-bold ${stats.net >= 0 ? 'text-blue-600' : 'text-orange-500'}`}>
                                ${stats.net.toLocaleString()}
                            </div>
                            <div className="text-xs text-gray-500">娣ㄩ</div>
                        </div>
                    </div>
                </div>

                {/* 鎻愮ず瑷婃伅 */}
                <div className="text-xs text-gray-500 flex items-center gap-2 p-3 bg-yellow-50 rounded-lg border border-yellow-100">
                    <FileSpreadsheet size={14} className="text-yellow-600" />
                    鍫辫〃灏囧尟鍑哄埌 Google Drive銆岃病鍕欏牨琛ㄣ€嶈硣鏂欏ぞ锛屼甫鎸夋湀浠借嚜鍕曞垎椤瀄r
                </div>
            </div>
        </Modal>
    );
};

// 鎼滃皨鏀舵敮瑷橀寗鍏冧欢
export const FinanceSearchBar = ({ onSearch, isSearching }) => {
    const [query, setQuery] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [showFilters, setShowFilters] = useState(false);

    const handleSearch = () => {
        if (query.trim()) {
            onSearch(query, { startDate, endDate });
        }
    };

    const handleClear = () => {
        setQuery('');
        setStartDate('');
        setEndDate('');
    };

    return (
        <div className="space-y-2">
            <div className="flex gap-2">
                <div className="relative flex-1">
                    <Search size={16} className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
                    <input
                        type="text"
                        value={query}
                        onChange={(e) => setQuery(e.target.value)}
                        onKeyDown={(e) => e.key === 'Enter' && handleSearch()}
                        placeholder="鎼滃皨浜ゆ槗瑷橀寗锛堣鏄庛€佸垎椤炪€佸皥妗堬級..."
                        className="w-full pl-10 pr-4 py-2.5 rounded-xl border border-gray-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 outline-none transition-all"
                    />
                </div>
                <button
                    onClick={() => setShowFilters(!showFilters)}
                    className={`px-3 py-2.5 rounded-xl border transition-colors ${showFilters ? 'bg-blue-50 border-blue-200 text-blue-600' : 'border-gray-200 text-gray-500 hover:bg-gray-50'
                        }`}
                >
                    <Filter size={18} />
                </button>
                <button
                    onClick={handleSearch}
                    disabled={isSearching || !query.trim()}
                    className="px-4 py-2.5 bg-blue-600 text-white rounded-xl font-medium hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                    {isSearching ? '鎼滃皨涓?..' : '鎼滃皨'}
                </button>
                {(query || startDate || endDate) && (
                    <button
                        onClick={handleClear}
                        className="px-3 py-2.5 text-gray-500 hover:text-gray-700"
                    >
                        <X size={18} />
                    </button>
                )}
            </div>

            {showFilters && (
                <div className="flex gap-3 p-3 bg-gray-50 rounded-lg">
                    <div className="flex-1">
                        <label className="block text-xs text-gray-500 mb-1">璧峰鏃ユ湡</label>
                        <input
                            type="date"
                            value={startDate}
                            onChange={(e) => setStartDate(e.target.value)}
                            className="w-full px-3 py-2 border rounded-lg text-sm focus:ring-2 focus:ring-blue-200 focus:border-blue-500 outline-none"
                        />
                    </div>
                    <div className="flex-1">
                        <label className="block text-xs text-gray-500 mb-1">绲愭潫鏃ユ湡</label>
                        <input
                            type="date"
                            value={endDate}
                            onChange={(e) => setEndDate(e.target.value)}
                            className="w-full px-3 py-2 border rounded-lg text-sm focus:ring-2 focus:ring-blue-200 focus:border-blue-500 outline-none"
                        />
                    </div>
                </div>
            )}
        </div>
    );
};

export default FinanceExportModal;

import React, { useState, useEffect, useMemo } from 'react';
import { Modal } from '../common/Modal';
import { InputField } from '../common/InputField';
import { LoanCalculator, calculateEqualPayment, calculateEqualPrincipal } from './LoanCalculator';
import { Building2, Percent, Calendar, Calculator } from 'lucide-react';

/**
 * 璨告甯虫埗鏂板/绶ㄨ集 Modal
 */

const COMMON_TERMS = [12, 24, 36, 60, 84, 120, 180, 240, 360];
const PAYMENT_DAYS = Array.from({ length: 28 }, (_, i) => i + 1);

export const LoanAccountModal = ({ isOpen, onClose, onConfirm, editingLoan = null }) => {
    const [formData, setFormData] = useState({
        bankName: '',
        principalAmount: '',
        interestRate: '',
        totalTerms: 240,
        paidTerms: 0,
        startDate: new Date().toISOString().split('T')[0],
        paymentDay: 15,
        paymentType: 'equalPayment',
        status: 'active'
    });

    // 鍒濆鍖栫法杓硣鏂橽r
    useEffect(() => {
        if (editingLoan) {
            setFormData({
                ...editingLoan,
                principalAmount: editingLoan.principalAmount?.toString() || '',
                interestRate: editingLoan.interestRate?.toString() || ''
            });
        } else {
            setFormData({
                bankName: '',
                principalAmount: '',
                interestRate: '',
                totalTerms: 240,
                paidTerms: 0,
                startDate: new Date().toISOString().split('T')[0],
                paymentDay: 15,
                paymentType: 'equalPayment',
                status: 'active'
            });
        }
    }, [editingLoan, isOpen]);

    // 鍗虫檪瑭︾畻
    const calculation = useMemo(() => {
        const principal = parseFloat(formData.principalAmount) || 0;
        const rate = parseFloat(formData.interestRate) || 0;
        const terms = parseInt(formData.totalTerms) || 0;

        if (formData.paymentType === 'equalPayment') {
            return calculateEqualPayment(principal, rate, terms);
        } else {
            return calculateEqualPrincipal(principal, rate, terms);
        }
    }, [formData.principalAmount, formData.interestRate, formData.totalTerms, formData.paymentType]);

    const handleSubmit = () => {
        // 椹楄瓑蹇呭～娆勪綅
        if (!formData.bankName || !formData.principalAmount || !formData.interestRate) {
            return;
        }

        const principal = parseFloat(formData.principalAmount);
        const rate = parseFloat(formData.interestRate);

        // 瑷堢畻鍓╅鏈噾
        let remainingPrincipal = principal;
        if (formData.paidTerms > 0 && calculation.schedule) {
            const paidIndex = Math.min(formData.paidTerms, calculation.schedule.length) - 1;
            if (paidIndex >= 0) {
                remainingPrincipal = calculation.schedule[paidIndex]?.remainingPrincipal || principal;
            }
        }

        const loanData = {
            ...formData,
            id: editingLoan?.id || `loan-${Date.now()}`,
            principalAmount: principal,
            interestRate: rate,
            monthlyPayment: formData.paymentType === 'equalPayment'
                ? calculation.monthlyPayment
                : calculation.firstPayment,
            remainingPrincipal,
            totalInterest: calculation.totalInterest
        };

        onConfirm(loanData);
    };

    const updateField = (field, value) => {
        setFormData(prev => ({ ...prev, [field]: value }));
    };

    return (
        <Modal
            isOpen={isOpen}
            onClose={onClose}
            title={editingLoan ? "绶ㄨ集璨告甯虫埗" : "鏂板璨告甯虫埗"}
            onConfirm={handleSubmit}
            confirmText={editingLoan ? "鍎插瓨璁婃洿" : "鏂板璨告"}
            size="lg"
        >
            <div className="space-y-4">
                {/* 閵€琛屽悕绋?*/}
                <InputField
                    label="璨告閵€琛孿

select"
                        value={formData.paymentDay}
                        onChange={e => updateField('paymentDay', parseInt(e.target.value))}
                    >
                        {PAYMENT_DAYS.map(day => (
                            <option key={day} value={day}>姣忔湀 {day} 鏃?/option>
                        ))}
                    </InputField>

                    <InputField
                        label="璧峰鏃ユ湡"
                        type="date"
                        value={formData.startDate}
                        onChange={e => updateField('startDate', e.target.value)}
                    />
                </div>

                {/* 閭勬鏂瑰紡 */}
                <div>
                    <label className="block text-sm font-medium text-gray-700 mb-2">閭勬鏂瑰紡</label>
                    <div className="flex gap-2 bg-gray-50 p-1 rounded-xl">
                        <button
                            type="button"
                            onClick={() => updateField('paymentType', 'equalPayment')}
                            className={`flex-1 py-2 px-4 rounded-lg text-sm font-medium transition-all ${formData.paymentType === 'equalPayment'
                                    ? 'bg-white text-blue-600 shadow-sm'
                                    : 'text-gray-500 hover:text-gray-700'
                                }`}
                        >
                            绛夐鏈伅
                            <div className="text-xs text-gray-400 mt-0.5">姣忔湀鍥哄畾閲戦</div>
                        </button>
                        <button
                            type="button"
                            onClick={() => updateField('paymentType', 'equalPrincipal')}
                            className={`flex-1 py-2 px-4 rounded-lg text-sm font-medium transition-all ${formData.paymentType === 'equalPrincipal'
                                    ? 'bg-white text-blue-600 shadow-sm'
                                    : 'text-gray-500 hover:text-gray-700'
                                }`}
                        >
                            绛夐鏈噾
                            <div className="text-xs text-gray-400 mt-0.5">姣忔湀閬炴笡閲戦</div>
                        </button>
                    </div>
                </div>

                {/* 宸查倓鏈熸暩锛堢法杓檪椤ず锛?*/}
                {editingLoan && (
                    <div className="grid grid-cols-2 gap-4">
                        <InputField
                            label="宸查倓鏈熸暩"
                            type="number"
                            min="0"
                            max={formData.totalTerms}
                            value={formData.paidTerms}
                            onChange={e => updateField('paidTerms', parseInt(e.target.value) || 0)}
                        />
                        <InputField
                            label="鐙€鎱媆

select"
                            value={formData.status}
                            onChange={e => updateField('status', e.target.value)}
                        >
                            <option value="active">閭勬涓?/option>
                            <option value="completed">宸茬祼娓?/option>
                        </InputField>
                    </div>
                )}

                {/* 鍗虫檪瑭︾畻绲愭灉 */}
                {formData.principalAmount && formData.interestRate && (
                    <LoanCalculator
                        principal={parseFloat(formData.principalAmount) || 0}
                        rate={parseFloat(formData.interestRate) || 0}
                        terms={formData.totalTerms}
                        paymentType={formData.paymentType}
                        showSchedule={true}
                        compact={false}
                    />
                )}
            </div>
        </Modal>
    );
};

export default LoanAccountModal;

import React, { useState, useMemo } from 'react';
import { Calculator, TrendingDown, Calendar } from 'lucide-react';

/**
 * 璨告閭勬瑭︾畻鍣╘r
 * 鏀彺绛夐鏈伅鍜岀瓑椤嶆湰閲戝叐绋倓娆炬柟寮廫r
 */

// 绛夐鏈伅瑷堢畻锛圥MT锛塡r
export const calculateEqualPayment = (principal, annualRate, months) => {
    if (months <= 0 || principal <= 0) return { monthlyPayment: 0, totalInterest: 0, totalPayment: 0, schedule: [] };

    const monthlyRate = annualRate / 100 / 12;

    if (monthlyRate === 0) {
        const monthlyPayment = principal / months;
        return {
            monthlyPayment: Math.round(monthlyPayment),
            totalInterest: 0,
            totalPayment: principal,
            schedule: Array.from({ length: months }, (_, i) => ({
                term: i + 1,
                payment: Math.round(monthlyPayment),
                principal: Math.round(monthlyPayment),
                interest: 0,
                remainingPrincipal: Math.round(principal - monthlyPayment * (i + 1))
            }))
        };
    }

    // PMT = P * [r(1+r)^n] / [(1+r)^n - 1]
    const factor = Math.pow(1 + monthlyRate, months);
    const monthlyPayment = principal * (monthlyRate * factor) / (factor - 1);

    let remaining = principal;
    const schedule = [];

    for (let i = 1; i <= months; i++) {
        const interest = remaining * monthlyRate;
        const principalPart = monthlyPayment - interest;
        remaining = remaining - principalPart;

        schedule.push({
            term: i,
            payment: Math.round(monthlyPayment),
            principal: Math.round(principalPart),
            interest: Math.round(interest),
            remainingPrincipal: Math.max(0, Math.round(remaining))
        });
    }

    const totalPayment = monthlyPayment * months;
    const totalInterest = totalPayment - principal;

    return {
        monthlyPayment: Math.round(monthlyPayment),
        totalInterest: Math.round(totalInterest),
        totalPayment: Math.round(totalPayment),
        schedule
    };
};

// 绛夐鏈噾瑷堢畻
export const calculateEqualPrincipal = (principal, annualRate, months) => {
    if (months <= 0 || principal <= 0) return { firstPayment: 0, lastPayment: 0, totalInterest: 0, totalPayment: 0, schedule: [] };

    const monthlyRate = annualRate / 100 / 12;
    const principalPerMonth = principal / months;

    let remaining = principal;
    const schedule = [];
    let totalInterest = 0;

    for (let i = 1; i <= months; i++) {
        const interest = remaining * monthlyRate;
        const payment = principalPerMonth + interest;
        remaining = remaining - principalPerMonth;
        totalInterest += interest;

        schedule.push({
            term: i,
            payment: Math.round(payment),
            principal: Math.round(principalPerMonth),
            interest: Math.round(interest),
            remainingPrincipal: Math.max(0, Math.round(remaining))
        });
    }

    return {
        firstPayment: Math.round(schedule[0]?.payment || 0),
        lastPayment: Math.round(schedule[months - 1]?.payment || 0),
        totalInterest: Math.round(totalInterest),
        totalPayment: Math.round(principal + totalInterest),
        schedule
    };
};

// 閭勬瑭︾畻鍣?UI 绲勪欢
export const LoanCalculator = ({
    principal = 0,
    rate = 0,
    terms = 0,
    paymentType = 'equalPayment',
    showSchedule = false,
    compact = false
}) => {
    const [showFullSchedule, setShowFullSchedule] = useState(false);

    const calculation = useMemo(() => {
        if (paymentType === 'equalPayment') {
            return calculateEqualPayment(principal, rate, terms);
        } else {
            return calculateEqualPrincipal(principal, rate, terms);
        }
    }, [principal, rate, terms, paymentType]);

    if (compact) {
        return (
            <div className="bg-blue-50 rounded-lg p-3 border border-blue-100">
                <div className="flex items-center gap-2 text-blue-700 mb-2">
                    <Calculator size={14} />
                    <span className="text-xs font-medium">閭勬瑭︾畻</span>
                </div>
                <div className="grid grid-cols-2 gap-2 text-sm">
                    <div>
                        <div className="text-xs text-gray-500">
                            {paymentType === 'equalPayment' ? '姣忔湀閭勬' : '棣栨湡閭勬'}
                        </div>
                        <div className="font-bold text-blue-700">
                            ${paymentType === 'equalPayment'
                                ? calculation.monthlyPayment?.toLocaleString()
                                : calculation.firstPayment?.toLocaleString()}
                        </div>
                    </div>
                    <div>
                        <div className="text-xs text-gray-500">绺藉埄鎭?/div>
                        <div className="font-bold text-orange-600">
                            ${calculation.totalInterest?.toLocaleString()}
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    return (
        <div className="bg-gradient-to-br from-blue-50 to-indigo-50 rounded-xl p-4 border border-blue-100">
            <div className="flex items-center gap-2 text-blue-700 mb-4">
                <Calculator size={18} />
                <span className="font-bold">閭勬瑭︾畻绲愭灉</span>
            </div>

            <div className="grid grid-cols-2 lg:grid-cols-4 gap-4 mb-4">
                <div className="bg-white rounded-lg p-3 shadow-sm">
                    <div className="text-xs text-gray-500 mb-1">
                        {paymentType === 'equalPayment' ? '姣忔湀閭勬' : '棣栨湡閭勬'}
                    </div>
                    <div className="text-lg font-bold text-blue-700">
                        ${paymentType === 'equalPayment'
                            ? calculation.monthlyPayment?.toLocaleString()
                            : calculation.firstPayment?.toLocaleString()}
                    </div>
                </div>

                {paymentType === 'equalPrincipal' && (
                    <div className="bg-white rounded-lg p-3 shadow-sm">
                        <div className="text-xs text-gray-500 mb-1">鏈湡閭勬</div>
                        <div className="text-lg font-bold text-green-600">
                            ${calculation.lastPayment?.toLocaleString()}
                        </div>
                    </div>
                )}

                <div className="bg-white rounded-lg p-3 shadow-sm">
                    <div className="text-xs text-gray-500 mb-1">绺藉埄鎭?/div>
                    <div className="text-lg font-bold text-orange-600">
                        ${calculation.totalInterest?.toLocaleString()}
                    </div>
                </div>

                <div className="bg-white rounded-lg p-3 shadow-sm">
                    <div className="text-xs text-gray-500 mb-1">绺介倓娆鹃噾椤?/div>
                    <div className="text-lg font-bold text-gray-800">
                        ${calculation.totalPayment?.toLocaleString()}
                    </div>
                </div>
            </div>

            {showSchedule && calculation.schedule.length > 0 && (
                <div className="mt-4">
                    <button
                        onClick={() => setShowFullSchedule(!showFullSchedule)}
                        className="text-sm text-blue-600 hover:underline flex items-center gap-1 mb-2"
                    >
                        <Calendar size={14} />
                        {showFullSchedule ? '鏀跺悎閭勬鏄庣窗' : `鏌ョ湅閭勬鏄庣窗 (${terms} 鏈?`}
                    </button>

                    {showFullSchedule && (
                        <div className="bg-white rounded-lg border border-gray-200 max-h-60 overflow-y-auto">
                            <table className="w-full text-xs">
                                <thead className="bg-gray-50 sticky top-0">
                                    <tr>
                                        <th className="px-2 py-2 text-left">鏈熸暩</th>
                                        <th className="px-2 py-2 text-right">閭勬閲戦</th>
                                        <th className="px-2 py-2 text-right">鏈噾</th>
                                        <th className="px-2 py-2 text-right">鍒╂伅</th>
                                        <th className="px-2 py-2 text-right">鍓╅鏈噾</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {calculation.schedule.slice(0, showFullSchedule ? undefined : 12).map(item => (
                                        <tr key={item.term} className="border-t border-gray-100 hover:bg-gray-50">
                                            <td className="px-2 py-1.5">{item.term}</td>
                                            <td className="px-2 py-1.5 text-right font-mono">${item.payment.toLocaleString()}</td>
                                            <td className="px-2 py-1.5 text-right font-mono text-blue-600">${item.principal.toLocaleString()}</td>
                                            <td className="px-2 py-1.5 text-right font-mono text-orange-500">${item.interest.toLocaleString()}</td>
                                            <td className="px-2 py-1.5 text-right font-mono text-gray-600">${item.remainingPrincipal.toLocaleString()}</td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    )}
                </div>
            )}
        </div>
    );
};

export default LoanCalculator;

