const TemplateItemsEditor = ({ items = [], onChange, chapters = [] }) => {
    // 瑷畾鎷栨洺鎰熸噳鍣╘r
    const sensors = useSensors(
        useSensor(PointerSensor, {
            activationConstraint: {
                distance: 8, // 闇€瑕佹嫋鏇?8px 鎵嶈Ц鐧糪r
            },
        }),
        useSensor(KeyboardSensor, {
            coordinateGetter: sortableKeyboardCoordinates,
        })
    );

    // 濡傛灉娌掓湁鎸囧畾绔犵瘈锛屽緸 items 涓帹鏂穃r
    const uniqueChapters = chapters.length > 0
        ? chapters
        : [...new Set(items.map(item => item.chapter || '鍏朵粬'))];

    // 瑷堢畻绺介噾椤峔r
    const totalAmount = items.reduce((sum, item) =>
        sum + ((item.quantity || 0) * (item.unitPrice || 0)), 0
    );

    const formatCurrency = (amount) => {
        return new Intl.NumberFormat('zh-TW', {
            style: 'currency',
            currency: 'TWD',
            minimumFractionDigits: 0,
        }).format(amount || 0);
    };

    // 铏曠悊鎷栨洺绲愭潫
    const handleDragEnd = (event) => {
        const { active, over } = event;

        if (active.id !== over?.id) {
            const oldIndex = items.findIndex((item) => item.id === active.id);
            const newIndex = items.findIndex((item) => item.id === over?.id);

            if (oldIndex !== -1 && newIndex !== -1) {
                const newItems = arrayMove(items, oldIndex, newIndex);
                onChange(newItems);
            }
        }
    };

    // 鏂板宸ラ爡
    const handleAddItem = (chapterIndex = 0) => {
        const newItem = {
            id: `new-${Date.now()}`,
            chapterIndex,
            chapter: uniqueChapters[chapterIndex] || '鍏朵粬',
            name: '',
            unit: '寮?,
            quantity: 1,
            unitPrice: 0,
            type: 'ITEM',
        };
        onChange([...items, newItem]);
    };

    // 绉婚櫎宸ラ爡
    const handleRemoveItem = (index) => {
        const newItems = items.filter((_, i) => i !== index);
        onChange(newItems);
    };

    // 鏇存柊宸ラ爡
    const handleUpdateItem = (index, field, value) => {
        const newItems = [...items];
        newItems[index] = { ...newItems[index], [field]: value };
        onChange(newItems);
    };

    // 纰轰繚姣忓€?item 閮芥湁鍞竴 ID
    const itemsWithIds = items.map((item, index) => ({
        ...item,
        id: item.id || `item-${index}-${Date.now()}`,
    }));

    // 绨″柈鍒楄〃妯″紡锛堢劇绔犵瘈鍒嗙祫锛塡r
    if (uniqueChapters.length <= 1 || !items.some(i => i.chapter)) {
        return (
            <div className="space-y-3">
                {/* 琛ㄩ牠 */}
                <div className="flex items-center gap-2 px-3 py-2 text-xs font-medium text-gray-500 uppercase">
                    <div className="w-6"></div>
                    <div className="w-8">#</div>
                    <div className="flex-1 min-w-[120px]">鍚嶇ū</div>
                    <div className="w-16 text-center">鍠綅</div>
                    <div className="w-20 text-right">鏁搁噺</div>
                    <div className="w-24 text-right">鍠児</div>
                    <div className="w-24 text-right">閲戦</div>
                    <div className="w-8"></div>
                </div>

                {/* 鍙嫋鏇冲伐闋呭垪琛?*/}
                <DndContext
                    sensors={sensors}
                    collisionDetection={closestCenter}
                    onDragEnd={handleDragEnd}
                >
                    <SortableContext
                        items={itemsWithIds.map(item => item.id)}
                        strategy={verticalListSortingStrategy}
                    >
                        <div className="space-y-2">
                            {itemsWithIds.map((item, index) => (
                                <SortableItemRow
                                    key={item.id}
                                    item={item}
                                    index={index}
                                    onUpdate={handleUpdateItem}
                                    onRemove={handleRemoveItem}
                                />
                            ))}
                        </div>
                    </SortableContext>
                </DndContext>

                {/* 鏂板鎸夐垥 */}
                <button
                    type="button"
                    onClick={() => handleAddItem()}
                    className="w-full py-3 text-sm text-gray-500 hover:text-orange-600 hover:bg-orange-50 border-2 border-dashed border-gray-200 rounded-xl transition-colors flex items-center justify-center gap-2"
                >
                    <Plus size={18} />
                    鏂板宸ラ爡
                </button>

                {/* 绺借▓ */}
                <div className="flex items-center justify-between p-4 bg-gradient-to-r from-orange-50 to-amber-50 rounded-xl border border-orange-100">
                    <div className="text-sm text-gray-600">
                        鍏?<span className="font-bold text-gray-800">{items.length}</span> 闋呭伐闋匼r
                        <span className="text-xs text-gray-400 ml-2">馃挕 鎷栨洺宸﹀伌鍦栫ず鍙帓搴?/span>
                    </div>
                    <div className="text-right">
                        <div className="text-xs text-gray-500">闋愪及绺介噾椤?/div>
                        <div className="text-xl font-bold text-orange-600">
                            {formatCurrency(totalAmount)}
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    // 绔犵瘈鍒嗙祫妯″紡
    return (
        <DndContext
            sensors={sensors}
            collisionDetection={closestCenter}
            onDragEnd={handleDragEnd}
        >
            <SortableContext
                items={itemsWithIds.map(item => item.id)}
                strategy={verticalListSortingStrategy}
            >
                <div className="space-y-4">
                    {uniqueChapters.map((chapter, chapterIndex) => (
                        <ChapterSection
                            key={chapter}
                            chapter={chapter}
                            chapterIndex={chapterIndex}
                            items={itemsWithIds}
                            onUpdateItem={handleUpdateItem}
                            onRemoveItem={handleRemoveItem}
                            onAddItem={handleAddItem}
                        />
                    ))}

                    {/* 绺借▓ */}
                    <div className="flex items-center justify-between p-4 bg-gradient-to-r from-orange-50 to-amber-50 rounded-xl border border-orange-100">
                        <div className="text-sm text-gray-600">
                            鍏?<span className="font-bold text-gray-800">{items.length}</span> 闋呭伐闋匼r
                            <span className="text-xs text-gray-400 ml-2">馃挕 鎷栨洺宸﹀伌鍦栫ず鍙帓搴?/span>
                        </div>
                        <div className="text-right">
                            <div className="text-xs text-gray-500">闋愪及绺介噾椤?/div>
                            <div className="text-xl font-bold text-orange-600">
                                {formatCurrency(totalAmount)}
                            </div>
                        </div>
                    </div>
                </div>
            </SortableContext>
        </DndContext>
    );
};

export default TemplateItemsEditor;

