/**
 * RequirePermission.jsx
 * 
 * RBAC Action Gating 鍏冧欢
 * 鏍规摎 permissions.actions 姹哄畾鏄惁椤ず瀛愬厓浠禱r
 */

import React from 'react';
import { useAuth } from '../../context/AuthContext';

/**
 * 妾㈡煡鏄惁鏈夐爜闈㈠瓨鍙栨瑠
 * @param {Object} permissions - 娆婇檺鐗╀欢
 * @param {string} pageKey - 闋侀潰 key
 * @returns {boolean}
 */
export function hasPageAccess(permissions, pageKey) {
    if (!permissions || !permissions.pages) return false;
    return permissions.pages.includes(pageKey);
}

/**
 * 妾㈡煡鏄惁鏈夋搷浣滄瑠闄怽r
 * @param {Object} permissions - 娆婇檺鐗╀欢
 * @param {string} domainKey - 娆婇檺鍩?key锛堝 integrations.google锛塡r
 * @param {string} action - 鎿嶄綔锛堝 connect锛塡r
 * @returns {boolean}
 */
export function hasAction(permissions, domainKey, action) {
    if (!permissions || !permissions.actions) return false;
    const domainActions = permissions.actions[domainKey];
    if (!domainActions || !Array.isArray(domainActions)) return false;
    return domainActions.includes(action);
}

/**
 * RequirePermission - RBAC Gating 鍏冧欢
 * 
 * 鐢ㄦ硶 1锛堥爜闈?gating锛夛細
 *   <RequirePermission pageKey="integrations">
 *     <IntegrationsContent />
 *   </RequirePermission>
 * 
 * 鐢ㄦ硶 2锛堟搷浣?gating锛夛細
 *   <RequirePermission domainKey="integrations.google" action="connect">
 *     <button>Connect</button>
 *   </RequirePermission>
 * 
 * @param {Object} props
 * @param {React.ReactNode} props.children
 * @param {string} [props.pageKey] - 闋侀潰 key锛堢敤鏂奸爜闈?gating锛塡r
 * @param {string} [props.domainKey] - 娆婇檺鍩?key锛堢敤鏂兼搷浣?gating锛塡r
 * @param {string} [props.action] - 鎿嶄綔锛堢敤鏂兼搷浣?gating锛塡r
 * @param {React.ReactNode} [props.fallback] - 鐒℃瑠闄愭檪椤ず鐨勫収瀹筡r
 * @param {Object} [props.permissions] - 鐩存帴鍌冲叆 permissions锛堝彲閬革級
 */
export function RequirePermission({
    children,
    pageKey,
    domainKey,
    action,
    fallback = null,
    permissions: propPermissions
}) {
    const { permissions: contextPermissions } = useAuth();
    const permissions = propPermissions || contextPermissions;

    // 闋侀潰 gating
    if (pageKey) {
        if (!hasPageAccess(permissions, pageKey)) {
            return fallback;
        }
        return children;
    }

    // 鎿嶄綔 gating
    if (domainKey && action) {
        if (!hasAction(permissions, domainKey, action)) {
            return fallback;
        }
        return children;
    }

    // 鐒?gating 瑕忓墖锛岀洿鎺ラ’绀篭r
    return children;
}

export default RequirePermission;

