                        <div className="min-w-0 flex-1">
                            <div className="text-xs font-medium text-gray-700 truncate">{f.name}</div>
                            <div className="text-[10px] text-gray-400">{f.size} 路 {f.date}</div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

messages.source:
/**
 * 宸ョ▼璁婃洿鍠爜闈?(ChangeOrders.jsx)
 * 璁婃洿鍠垪琛ㄨ垏绶ㄨ集鍣╘r
 */

import React, { useState, useEffect } from 'react';
import {
    ChangeOrderService,
    CHANGE_ORDER_STATUS,
    CHANGE_ORDER_STATUS_LABELS,
    CHANGE_ORDER_STATUS_COLORS,
    CHANGE_TYPES,
    CHANGE_TYPE_LABELS,
    CHANGE_TYPE_COLORS,
    CHANGE_REASONS,
    calculateChangeOrderTotals,
} from '../services/ChangeOrderService';
import { QuotationService } from '../services/QuotationService';

// ============================================
// 鏍煎紡鍖栧嚱鏁竆r
// ============================================
const formatCurrency = (amount) => {
    return new Intl.NumberFormat('zh-TW', {
        style: 'currency',
        currency: 'TWD',
        minimumFractionDigits: 0,
        maximumFractionDigits: 0,
    }).format(amount || 0);
};

const formatDate = (dateString) => {
    if (!dateString) return '-';
    return new Date(dateString).toLocaleDateString('zh-TW');
};

// ============================================
// 鐙€鎱嬪窘绔燶r
// ============================================
const StatusBadge = ({ status }) => (
    <span className={`px-2 py-1 rounded-full text-xs font-medium ${CHANGE_ORDER_STATUS_COLORS[status]}`}>
        {CHANGE_ORDER_STATUS_LABELS[status]}
    </span>
);

const ChangeTypeBadge = ({ type }) => (
    <span className={`px-2 py-1 rounded-full text-xs font-medium ${CHANGE_TYPE_COLORS[type]}`}>
        {CHANGE_TYPE_LABELS[type]}
    </span>
);

// ============================================
// 璁婃洿闋呯洰鍒梊r
// ============================================
const ChangeItemRow = ({ item, index, onUpdate, onDelete, disabled }) => {
    const amount = (parseFloat(item.quantity) || 0) * (parseFloat(item.unitPrice) || 0);
    const originalAmount = (item.originalQuantity || 0) * (item.originalUnitPrice || 0);
    const diff = amount - originalAmount;

    return (
        <tr className="border-b border-gray-100 hover:bg-gray-50">
            <td className="py-3 px-4">
                <select
                    value={item.changeType}
                    onChange={(e) => onUpdate(index, 'changeType', e.target.value)}
                    disabled={disabled}
                    className="w-full px-2 py-1 border rounded text-sm"
                >
                    {Object.entries(CHANGE_TYPE_LABELS).map(([key, label]) => (
                        <option key={key} value={key}>{label}</option>
                    ))}
                </select>
            </td>
            <td className="py-3 px-4">
                <input
                    type="text"
                    value={item.name}
                    onChange={(e) => onUpdate(index, 'name', e.target.value)}
                    disabled={disabled}
                    placeholder="闋呯洰鍚嶇ū"
                    className="w-full px-2 py-1 border rounded text-sm"
                />
            </td>
            <td className="py-3 px-4">
                <input
                    type="text"
                    value={item.specification || ''}
                    onChange={(e) => onUpdate(index, 'specification', e.target.value)}
                    disabled={disabled}
                    placeholder="瑕忔牸"
                    className="w-full px-2 py-1 border rounded text-sm"
                />
            </td>
            <td className="py-3 px-4">
                <input
                    type="text"
                    value={item.unit}
                    onChange={(e) => onUpdate(index, 'unit', e.target.value)}
                    disabled={disabled}
                    className="w-20 px-2 py-1 border rounded text-sm text-center"
                />
            </td>
            <td className="py-3 px-4">
                <input
                    type="number"
                    value={item.quantity}
                    onChange={(e) => onUpdate(index, 'quantity', parseFloat(e.target.value) || 0)}
                    disabled={disabled}
                    className="w-20 px-2 py-1 border rounded text-sm text-right"
                />
            </td>
            <td className="py-3 px-4">
                <input
                    type="number"
                    value={item.unitPrice}
                    onChange={(e) => onUpdate(index, 'unitPrice', parseFloat(e.target.value) || 0)}
                    disabled={disabled}
                    className="w-24 px-2 py-1 border rounded text-sm text-right"
                />
            </td>
            <td className="py-3 px-4 text-right font-medium">
                {formatCurrency(amount)}
            </td>
            <td className="py-3 px-4 text-right">
                {item.changeType === CHANGE_TYPES.MODIFY && (
                    <span className={diff >= 0 ? 'text-green-600' : 'text-red-600'}>
                        {diff >= 0 ? '+' : ''}{formatCurrency(diff)}
                    </span>
                )}
            </td>
            <td className="py-3 px-4">
                {!disabled && (
                    <button
                        onClick={() => onDelete(index)}
                        className="text-red-500 hover:text-red-700"
                    >
                        馃棏锔廫r
                    </button>
                )}
            </td>
        </tr>
    );
};

// ============================================
// 璁婃洿鍠法杓櫒
// ============================================
const ChangeOrderEditor = ({ changeOrder, quotation, onSave, onBack, addToast }) => {
    const [formData, setFormData] = useState({
        title: changeOrder?.title || '',
        description: changeOrder?.description || '',
        reason: changeOrder?.reason || 'client_request',
        items: changeOrder?.items || [],
    });
    const [isSaving, setIsSaving] = useState(false);
    const [quotationItems, setQuotationItems] = useState([]);

    useEffect(() => {
        if (quotation?.items) {
            setQuotationItems(quotation.items.filter(i => i.type === 'ITEM'));
        }
    }, [quotation]);

    const isEditable = !changeOrder || changeOrder.status === CHANGE_ORDER_STATUS.DRAFT;

    const totals = calculateChangeOrderTotals(formData.items);
    const originalAmount = changeOrder?.originalContractAmount || quotation?.totalAmount || 0;
    const newAmount = originalAmount + totals.netChange;

    const handleAddItem = () => {
        const newItem = ChangeOrderService.createNewChangeItem();
        setFormData(prev => ({
            ...prev,
            items: [...prev.items, newItem],
        }));
    };

    const handleAddFromQuotation = (quotationItem, changeType) => {
        const newItem = ChangeOrderService.createChangeItemFromQuotation(quotationItem, changeType);
        setFormData(prev => ({
            ...prev,
            items: [...prev.items, newItem],
        }));
    };

    const handleUpdateItem = (index, field, value) => {
        setFormData(prev => ({
            ...prev,
            items: prev.items.map((item, i) =>
                i === index ? { ...item, [field]: value } : item
            ),
        }));
    };

    const handleDeleteItem = (index) => {
        setFormData(prev => ({
            ...prev,
            items: prev.items.filter((_, i) => i !== index),
        }));
    };

    const handleSave = async () => {
        if (!formData.title.trim()) {
            addToast?.('error', '璜嬭几鍏ヨ畩鏇村柈妯欓');
            return;
        }
        if (formData.items.length === 0) {
            addToast?.('error', '璜嬫柊澧炶嚦灏戜竴鍊嬭畩鏇撮爡鐩?);
            return;
        }

        setIsSaving(true);
        try {
            if (changeOrder?.id) {
                await ChangeOrderService.updateChangeOrder(changeOrder.id, formData);
            } else {
                await ChangeOrderService.createChangeOrder({
                    ...formData,
                    quotationId: quotation.id,
                });
            }
            addToast?.('success', '鍎插瓨鎴愬姛');
            onSave?.();
        } catch (error) {
            console.error('Save error:', error);
            addToast?.('error', '鍎插瓨澶辨晽: ' + error.message);
        } finally {
            setIsSaving(false);
        }
    };

    const handleSubmit = async () => {
        await handleSave();
        try {
            await ChangeOrderService.submitForReview(changeOrder.id, 'current-user');
            addToast?.('success', '宸查€佸嚭瀵╂牳');
            onSave?.();
        } catch (error) {
            addToast?.('error', '閫佸嚭澶辨晽');
        }
    };

    return (
        <div className="space-y-6">
            {/* 闋傞儴灏庤埅 */}
            <div className="flex items-center justify-between">
                <div className="flex items-center gap-4">
                    <button
                        onClick={onBack}
                        className="p-2 hover:bg-gray-100 rounded-lg"
                    >
                        鈫?杩斿洖
                    </button>
                    <div>
                        <h2 className="text-xl font-bold">
                            {changeOrder?.changeOrderNo || '鏂板璁婃洿鍠?}
                        </h2>
                        <p className="text-sm text-gray-500">
                            {quotation?.quotationNo} - {quotation?.title}
                        </p>
                    </div>
                    {changeOrder && <StatusBadge status={changeOrder.status} />}
                </div>
                <div className="flex gap-2">
                    {isEditable && (
                        <>
                            <button
                                onClick={handleSave}
                                disabled={isSaving}
                                className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 disabled:opacity-50"
                            >
                                {isSaving ? '鍎插瓨涓?..' : '鍎插瓨'}
                            </button>
                            {changeOrder?.id && (
                                <button
                                    onClick={handleSubmit}
                                    className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700"
                                >
                                    閫佸嚭瀵╂牳
                                </button>
                            )}
                        </>
                    )}
                </div>
            </div>

            {/* 鍩烘湰璩囪▕ */}
            <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
                <h3 className="font-semibold mb-4">鍩烘湰璩囪▕</h3>
                <div className="grid grid-cols-3 gap-4">
                    <div>
                        <label className="block text-sm text-gray-600 mb-1">璁婃洿鍠椤?/label>
                        <input
                            type="text"
                            value={formData.title}
                            onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
                            disabled={!isEditable}
                            placeholder="渚嬶細杩藉姞闅旈枔鐗嗗伐绋媆

w-full px-3 py-2 border rounded-lg"
                        />
                    </div>
                    <div>
                        <label className="block text-sm text-gray-600 mb-1">璁婃洿鍘熷洜</label>
                        <select
                            value={formData.reason}
                            onChange={(e) => setFormData(prev => ({ ...prev, reason: e.target.value }))}
                            disabled={!isEditable}
                            className="w-full px-3 py-2 border rounded-lg"
                        >
                            {CHANGE_REASONS.map(r => (
                                <option key={r.id} value={r.id}>{r.icon} {r.label}</option>
                            ))}
                        </select>
                    </div>
                    <div>
                        <label className="block text-sm text-gray-600 mb-1">瑾槑</label>
                        <input
                            type="text"
                            value={formData.description}
                            onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
                            disabled={!isEditable}
                            placeholder="璁婃洿鍘熷洜瑾槑"
                            className="w-full px-3 py-2 border rounded-lg"
                        />
                    </div>
                </div>
            </div>

            {/* 璁婃洿闋呯洰 */}
            <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
                <div className="flex justify-between items-center mb-4">
                    <h3 className="font-semibold">璁婃洿闋呯洰</h3>
                    {isEditable && (
                        <div className="flex gap-2">
                            <button
                                onClick={handleAddItem}
                                className="px-3 py-1 bg-green-100 text-green-700 rounded-lg hover:bg-green-200 text-sm"
                            >
                                鉃?鏂板闋呯洰
                            </button>
                        </div>
                    )}
                </div>

                {/* 寰炲師浼板児鍠伕鍙?*/}
                {isEditable && quotationItems.length > 0 && (
                    <div className="mb-4 p-4 bg-gray-50 rounded-lg">
                        <p className="text-sm text-gray-600 mb-2">寰炲師浼板児鍠伕鍙栧伐闋咃細</p>
                        <div className="flex flex-wrap gap-2">
                            {quotationItems.slice(0, 10).map(item => (
                                <div key={item.id} className="flex items-center gap-1 bg-white px-2 py-1 rounded border text-sm">
                                    <span>{item.name}</span>
                                    <button
                                        onClick={() => handleAddFromQuotation(item, CHANGE_TYPES.MODIFY)}
                                        className="text-blue-500 hover:text-blue-700"
                                        title="璁婃洿"
                                    >
                                        馃摑
                                    </button>
                                    <button
                                        onClick={() => handleAddFromQuotation(item, CHANGE_TYPES.DEDUCT)}
                                        className="text-red-500 hover:text-red-700"
                                        title="娓涢爡"
                                    >
                                        鉃朶r
                                    </button>
                                </div>
                            ))}
                        </div>
                    </div>
                )}

                {/* 闋呯洰琛ㄦ牸 */}
                <div className="overflow-x-auto">
                    <table className="w-full">
                        <thead className="bg-gray-50">
                            <tr>
                                <th className="py-3 px-4 text-left text-sm font-medium text-gray-600">椤炲瀷</th>
                                <th className="py-3 px-4 text-left text-sm font-medium text-gray-600">闋呯洰鍚嶇ū</th>
                                <th className="py-3 px-4 text-left text-sm font-medium text-gray-600">瑕忔牸</th>
                                <th className="py-3 px-4 text-center text-sm font-medium text-gray-600">鍠綅</th>
                                <th className="py-3 px-4 text-right text-sm font-medium text-gray-600">鏁搁噺</th>
                                <th className="py-3 px-4 text-right text-sm font-medium text-gray-600">鍠児</th>
                                <th className="py-3 px-4 text-right text-sm font-medium text-gray-600">閲戦</th>
                                <th className="py-3 px-4 text-right text-sm font-medium text-gray-600">宸</th>
                                <th className="py-3 px-4 w-12"></th>
                            </tr>
                        </thead>
                        <tbody>
                            {formData.items.map((item, index) => (
                                <ChangeItemRow
                                    key={item.id}
                                    item={item}
                                    index={index}
                                    onUpdate={handleUpdateItem}
                                    onDelete={handleDeleteItem}
                                    disabled={!isEditable}
                                />
                            ))}
                        </tbody>
                    </table>
                    {formData.items.length === 0 && (
                        <div className="text-center py-8 text-gray-400">
                            灏氱劇璁婃洿闋呯洰锛岃珛榛炴搳銆屾柊澧為爡鐩€嶆垨寰炲師浼板児鍠伕鍙朶r
                        </div>
                    )}
                </div>
            </div>

            {/* 閲戦鎽樿 */}
            <div className="bg-white rounded-xl p-6 shadow-sm border border-gray-100">
                <h3 className="font-semibold mb-4">閲戦鎽樿</h3>
                <div className="grid grid-cols-4 gap-4">
                    <div className="p-4 bg-gray-50 rounded-lg text-center">
                        <p className="text-sm text-gray-500">鍘熷悎绱勯噾椤?/p>
                        <p className="text-xl font-bold">{formatCurrency(originalAmount)}</p>
                    </div>
                    <div className="p-4 bg-green-50 rounded-lg text-center">
                        <p className="text-sm text-green-600">杩藉姞閲戦</p>
                        <p className="text-xl font-bold text-green-600">+{formatCurrency(totals.totalAdded)}</p>
                    </div>
                    <div className="p-4 bg-red-50 rounded-lg text-center">
                        <p className="text-sm text-red-600">娓涢爡閲戦</p>
                        <p className="text-xl font-bold text-red-600">-{formatCurrency(totals.totalDeducted)}</p>
                    </div>
                    <div className="p-4 bg-blue-50 rounded-lg text-center">
                        <p className="text-sm text-blue-600">璁婃洿寰岀附鍍?/p>
                        <p className="text-xl font-bold text-blue-600">{formatCurrency(newAmount)}</p>
                    </div>
                </div>
            </div>
        </div>
    );
};

// ============================================
// 璁婃洿鍠垪琛╘r
// ============================================
const ChangeOrderList = ({ quotationId, onEdit, onBack, addToast }) => {
    const [changeOrders, setChangeOrders] = useState([]);
    const [quotation, setQuotation] = useState(null);
    const [loading, setLoading] = useState(true);
    const [cumulative, setCumulative] = useState({ totalAdded: 0, totalDeducted: 0, netChange: 0, count: 0 });

    const loadData = async () => {
        try {
            const [orders, quo, cum] = await Promise.all([
                ChangeOrderService.getChangeOrders(quotationId),
                QuotationService.getQuotation(quotationId),
                ChangeOrderService.getCumulativeChanges(quotationId),
            ]);
            setChangeOrders(orders);
            setQuotation(quo);
            setCumulative(cum);
        } catch (error) {
            console.error('Load error:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        loadData();
    }, [quotationId]);

    const handleCreate = async () => {
        const newOrder = await ChangeOrderService.createChangeOrder({
            quotationId,
            title: `绗?${changeOrders.length + 1} 娆¤畩鏇碻,
        });
        onEdit(newOrder, quotation);
    };

    const handleDelete = async (id) => {
        if (!window.confirm('纰哄畾瑕佸埅闄ゆ璁婃洿鍠紵')) return;
        try {
            await ChangeOrderService.deleteChangeOrder(id);
            addToast?.('success', '宸插埅闄?);
            loadData();
        } catch (error) {
            addToast?.('error', error.message);
        }
    };

    if (loading) {
        return <div className="text-center py-8">杓夊叆涓?..</div>;
    }

    return (
        <div className="space-y-6">
            {/* 闋傞儴 */}
            <div className="flex items-center justify-between">
                <div className="flex items-center gap-4">
                    <button onClick={onBack} className="p-2 hover:bg-gray-100 rounded-lg">
                        鈫?杩斿洖
                    </button>
                    <div>
                        <h2 className="text-xl font-bold">宸ョ▼璁婃洿鍠?/h2>
                        <p className="text-sm text-gray-500">
                            {quotation?.quotationNo} - {quotation?.title}
                        </p>
                    </div>
                </div>
                <button
                    onClick={handleCreate}
                    className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700"
                >
                    鉃?鏂板璁婃洿鍠甛r
                </button>
            </div>

            {/* 绱▓绲辫▓ */}
            <div className="grid grid-cols-4 gap-4">
                <div className="bg-white rounded-xl p-4 shadow-sm border border-gray-100 text-center">
                    <p className="text-sm text-gray-500">璁婃洿鍠暩閲?/p>
                    <p className="text-2xl font-bold">{cumulative.count}</p>
                </div>
                <div className="bg-white rounded-xl p-4 shadow-sm border border-gray-100 text-center">
                    <p className="text-sm text-green-600">绱▓杩藉姞</p>
                    <p className="text-2xl font-bold text-green-600">+{formatCurrency(cumulative.totalAdded)}</p>
                </div>
                <div className="bg-white rounded-xl p-4 shadow-sm border border-gray-100 text-center">
                    <p className="text-sm text-red-600">绱▓娓涢爡</p>
                    <p className="text-2xl font-bold text-red-600">-{formatCurrency(cumulative.totalDeducted)}</p>
                </div>
                <div className="bg-white rounded-xl p-4 shadow-sm border border-gray-100 text-center">
                    <p className="text-sm text-blue-600">娣ㄨ畩鏇?/p>
                    <p className={`text-2xl font-bold ${cumulative.netChange >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                        {cumulative.netChange >= 0 ? '+' : ''}{formatCurrency(cumulative.netChange)}
                    </p>
                </div>
            </div>

            {/* 璁婃洿鍠垪琛?*/}
            <div className="bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden">
                <table className="w-full">
                    <thead className="bg-gray-50">
                        <tr>
                            <th className="py-3 px-4 text-left text-sm font-medium text-gray-600">璁婃洿鍠櫉</th>
                            <th className="py-3 px-4 text-left text-sm font-medium text-gray-600">妯欓</th>
                            <th className="py-3 px-4 text-left text-sm font-medium text-gray-600">鍘熷洜</th>
                            <th className="py-3 px-4 text-center text-sm font-medium text-gray-600">鐙€鎱?/th>
                            <th className="py-3 px-4 text-right text-sm font-medium text-gray-600">娣ㄨ畩鏇?/th>
                            <th className="py-3 px-4 text-left text-sm font-medium text-gray-600">寤虹珛鏃ユ湡</th>
                            <th className="py-3 px-4 text-center text-sm font-medium text-gray-600">鎿嶄綔</th>
                        </tr>
                    </thead>
                    <tbody>
                        {changeOrders.map(order => {
                            const reason = CHANGE_REASONS.find(r => r.id === order.reason);
                            return (
                                <tr key={order.id} className="border-b border-gray-100 hover:bg-gray-50">
                                    <td className="py-3 px-4 font-medium">{order.changeOrderNo}</td>
                                    <td className="py-3 px-4">{order.title}</td>
                                    <td className="py-3 px-4 text-sm">
                                        {reason?.icon} {reason?.label}
                                    </td>
                                    <td className="py-3 px-4 text-center">
                                        <StatusBadge status={order.status} />
                                    </td>
                                    <td className={`py-3 px-4 text-right font-medium ${order.netChange >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                                        {order.netChange >= 0 ? '+' : ''}{formatCurrency(order.netChange)}
                                    </td>
                                    <td className="py-3 px-4 text-sm text-gray-500">
                                        {formatDate(order.createdAt)}
                                    </td>
                                    <td className="py-3 px-4 text-center">
                                        <div className="flex justify-center gap-2">
                                            <button
                                                onClick={() => onEdit(order, quotation)}
                                                className="px-2 py-1 text-sm text-blue-600 hover:bg-blue-50 rounded"
                                            >
                                                {order.status === CHANGE_ORDER_STATUS.DRAFT ? '绶ㄨ集' : '鏌ョ湅'}
                                            </button>
                                            {order.status === CHANGE_ORDER_STATUS.DRAFT && (
                                                <button
                                                    onClick={() => handleDelete(order.id)}
