                {/* 鏂板鑱怠瑷橀寗 Modal */}
                <Modal
                    isOpen={isContactLogModalOpen}
                    onClose={() => setIsContactLogModalOpen(false)}
                    title="鏂板鑱怠瑷橀寗"
                    onConfirm={handleAddContactLog}
                >
                    <div className="space-y-4">
                        <div className="grid grid-cols-2 gap-4">
                            <div>
                                <label className="block text-sm text-gray-500 mb-1">椤炲瀷</label>
                                <select
                                    value={newContactLog.type}
                                    onChange={e => setNewContactLog({ ...newContactLog, type: e.target.value })}
                                    className="w-full border rounded-lg px-3 py-2 bg-white"
                                >
                                    {['闆昏┍鑱公', '鐝惧牬鎷滆í', 'LINE瑷婃伅', 'Email', '鏈冭', '鍏朵粬'].map(t => (
                                        <option key={t} value={t}>{t}</option>
                                    ))}
                                </select>
                            </div>
                            <InputField
                                label="鏃ユ湡"
                                type="date"
                                value={newContactLog.date}
                                onChange={e => setNewContactLog({ ...newContactLog, date: e.target.value })}
                            />
                        </div>
                        <div>
                            <label className="block text-sm text-gray-500 mb-1">鍏у</label>
                            <textarea
                                value={newContactLog.note}
                                onChange={e => setNewContactLog({ ...newContactLog, note: e.target.value })}
                                placeholder="瑷橀寗鑱怠鍏у..."
                                className="w-full border rounded-lg px-3 py-2 min-h-[100px] resize-none"
                            />
                        </div>
                    </div>
                </Modal>
            </div>
        );
    }

    // 瀹㈡埗鍒楄〃闋乗r
    return (
        <div className="space-y-6 animate-fade-in">
            <SectionTitle title="瀹㈡埗绠＄悊" />

            {loading ? <LoadingSkeleton /> : (
                <>
                    {/* 绲辫▓鍗＄墖 */}
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-3 sm:gap-4">
                        <StatCard icon={User} label="绺藉鎴舵暩" value={stats.total} color="gray" onClick={() => setStatusFilter('鍏ㄩ儴')} />
                        <StatCard icon={Clock} label="娲借珖涓璡

yellow" onClick={() => setStatusFilter('宸插畬宸?)} />
                    </div>

                    {/* 鎼滃皨鑸囩閬稿垪 */}
                    <div className="flex flex-wrap items-center gap-3">
                        <div className="relative flex-1 min-w-[200px]">
                            <Search size={18} className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
                            <input
                                type="text"
                                value={searchTerm}
                                onChange={(e) => setSearchTerm(e.target.value)}
                                placeholder="鎼滃皨瀹㈡埗濮撳悕銆侀浕瑭便€丒mail..."
                                className="w-full pl-10 pr-4 py-2.5 rounded-xl border border-gray-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-200 outline-none transition-all"
                            />
                        </div>
                        <select
                            value={statusFilter}
                            onChange={(e) => setStatusFilter(e.target.value)}
                            className="px-4 py-2.5 rounded-xl border border-gray-200 bg-white focus:border-blue-500 outline-none"
                        >
                            <option value="鍏ㄩ儴">鍏ㄩ儴鐙€鎱?/option>
                            {Object.keys(STATUS_CONFIG).map(status => (
                                <option key={status} value={status}>{status}</option>
                            ))}
                        </select>
                        {(searchTerm || statusFilter !== '鍏ㄩ儴') && (
                            <button
                                onClick={() => { setSearchTerm(''); setStatusFilter('鍏ㄩ儴'); }}
                                className="px-3 py-2.5 text-gray-500 hover:text-gray-700 flex items-center gap-1"
                            >
                                <X size={16} /> 娓呴櫎
                            </button>
                        )}
                        <button
                            onClick={handleOpenAdd}
                            className="ml-auto bg-morandi-text-accent text-white px-4 py-2.5 rounded-xl text-sm font-medium hover:shadow-lg transition-all flex items-center gap-2"
                        >
                            <Plus size={16} /> 鏂板瀹㈡埗
                        </button>
                    </div>

                    {/* 瀹㈡埗鍒楄〃 */}
                    <div className="space-y-3">
                        {filteredClients.length > 0 ? (
                            filteredClients.map(client => (
                                <ClientRow
                                    key={client.id}
                                    client={client}
                                    onSelect={setActiveClient}
                                    onDelete={handleDeleteClient}
                                />
                            ))
                        ) : (
                            <div className="text-center py-12 text-gray-400">
                                {searchTerm || statusFilter !== '鍏ㄩ儴' ? '鐒＄鍚堟浠剁殑瀹㈡埗' : '灏氱劇瀹㈡埗璩囨枡'}
                            </div>
                        )}
                    </div>
                </>
            )}

            {/* 鏂板瀹㈡埗 Modal */}
            <Modal
                isOpen={isAddModalOpen}
                onClose={() => { setIsAddModalOpen(false); setIsSaving(false); }}
                title="鏂板瀹㈡埗"
                onConfirm={handleSaveNewClient}
                confirmDisabled={isSaving}
                confirmText={isSaving ? '铏曠悊涓?..' : '纰哄畾'}
            >
                <InputField label="濮撳悕" value={newClientData.name} onChange={e => setNewClientData({ ...newClientData, name: e.target.value })} placeholder="蹇呭～" />
                <div className="grid grid-cols-1 sm:grid-cols-2 gap-4">
                    <InputField label="闆昏┍" value={newClientData.phone} onChange={e => setNewClientData({ ...newClientData, phone: e.target.value })} placeholder="渚嬶細0912-345-678" />
                    <InputField label="Email" value={newClientData.email} onChange={e => setNewClientData({ ...newClientData, email: e.target.value })} placeholder="渚嬶細test@email.com" />
                </div>
                <LocationField label="鍦板潃" value={newClientData.address} onChange={e => setNewClientData({ ...newClientData, address: e.target.value })} placeholder="渚嬶細鍙板寳甯備俊缇╁崁鏉炬櫤璺?铏焅

LINE ID" value={newClientData.lineId} onChange={e => setNewClientData({ ...newClientData, lineId: e.target.value })} placeholder="渚嬶細@lineid 鎴?鍚嶇ū" />
                <div className="border-t border-gray-100 pt-4 mt-4">
                    <h4 className="text-sm font-bold text-gray-700 mb-3">鑷▊娆勪綅</h4>
                    <DynamicFieldEditor fields={newClientData.customFields} onChange={(newFields) => setNewClientData({ ...newClientData, customFields: newFields })} />
                </div>
                <div className="mt-4 p-3 bg-blue-50 text-blue-700 text-xs rounded-lg flex items-center gap-2">
                    <Cloud size={14} /> 绯荤当灏囪嚜鍕曟柤 Google Drive 寤虹珛灏堝爆璩囨枡澶綷r
                </div>
            </Modal>
        </div>
    );
};

export default Clients;

