        completed: contracts.filter(c => c.status === CONTRACT_STATUS.COMPLETED || c.status === CONTRACT_STATUS.WARRANTY).length,
        totalAmount: contracts.reduce((sum, c) => sum + (c.currentAmount || 0), 0),
    }), [contracts]);

