import React, { useState, useEffect, useMemo } from 'react';
import {
    Package, Plus, Search, Filter, Edit2, Trash2,
    ArrowDownCircle, ArrowUpCircle, AlertTriangle,
    CheckCircle, XCircle, X, Save, History, MapPin,
    BarChart3, TrendingDown, TrendingUp, Box,
    FileSpreadsheet, ExternalLink, RefreshCw
} from 'lucide-react';
import { Badge } from '../components/common/Badge';
import { Modal } from '../components/common/Modal';
import { InputField } from '../components/common/InputField';
import { SectionTitle } from '../components/common/Indicators';
import { GoogleService } from '../services/GoogleService';

// 搴瓨椤炲垾 - 鍏╁堡绲愭
const CATEGORY_TREE = {
    '绲愭鏉挎潗': ['鏈ㄦ枡', '鏉挎潗', '闃茬伀鏉?, '杓曢嫾鏋?],
    '瑁濋＞寤烘潗': ['鍦伴潰鏉愭枡', '鐗嗛潰鏉愭枡', '澶╄姳鏉愭枡', '濉楁枡娌规紗'],
    '姗熼浕瑷倷': ['闆绘埃闆荤窔', '鐕堝叿鐓ф槑', '闁嬮棞鎻掑骇', '寮遍浕瑷倷', '姘寸琛涙荡'],
    '浜旈噾閰嶄欢': ['闁€绐椾簲閲?, '娅冮珨浜旈噾', '绲愭浜旈噾', '瑁濋＞浜旈噾'],
    '娑堣€楀搧': ['榛忚憲鍔?, '濉斧鏉愭枡', '闃茶鐢ㄥ搧', '娓呮綌鐢ㄥ搧']
};

// 涓婚鍒ュ垪琛紙鍚€屽叏閮ㄣ€嶉伕闋咃級
const MAIN_CATEGORIES = ['鍏ㄩ儴', ...Object.keys(CATEGORY_TREE)];

// 鎵€鏈夊瓙椤炲垾鐨勬墎骞冲垪琛╘r
const ALL_SUB_CATEGORIES = Object.values(CATEGORY_TREE).flat();

// 鏍规摎涓婚鍒ョ嵅鍙栧瓙椤炲垾
const getSubCategories = (mainCategory) => {
    if (mainCategory === '鍏ㄩ儴' || !mainCategory) return ALL_SUB_CATEGORIES;
    return CATEGORY_TREE[mainCategory] || [];
};

// 鏍规摎瀛愰鍒ユ壘涓婚鍒r
const getMainCategory = (subCategory) => {
    for (const [main, subs] of Object.entries(CATEGORY_TREE)) {
        if (subs.includes(subCategory)) return main;
    }
    return '娑堣€楀搧'; // 闋愯ō
};

// 鐙€鎱嬮伕闋匼r
const STATUS_OPTIONS = ['鍏ㄩ儴', '鍏呰冻', '搴瓨鍋忎綆', '缂鸿波'];


// 鐙€鎱嬮鑹瞈r
const getStatusColor = (status) => {
    switch (status) {
        case '缂鸿波': return 'red';
        case '搴瓨鍋忎綆': return 'orange';
        case '鍏呰冻': return 'green';
        default: return 'gray';
    }
};

// 瑷堢畻鐙€鎱媆r
const calculateStatus = (quantity, safeStock) => {
    if (quantity <= 0) return '缂鸿波';
    if (quantity < safeStock) return '搴瓨鍋忎綆';
    return '鍏呰冻';
};

// 鏍煎紡鍖栨棩鏈焅r
const formatDate = (dateStr) => {
    if (!dateStr) return '-';
    return new Date(dateStr).toLocaleDateString('zh-TW');
};

// 绲辫▓鍗＄墖绲勪欢
const StatCard = ({ icon: Icon, label, value, color = 'gray', onClick }) => (
    <div
        onClick={onClick}
        className={`bg-white rounded-xl p-4 border border-gray-100 shadow-sm cursor-pointer hover:shadow-md transition-all ${onClick ? 'cursor-pointer' : ''}`}
    >
        <div className="flex items-center justify-between">
            <div className={`w-10 h-10 rounded-lg bg-${color}-100 flex items-center justify-center`}>
                <Icon size={20} className={`text-${color}-600`} />
            </div>
            <div className="text-right">
                <div className={`text-2xl font-bold text-${color}-600`}>{value}</div>
                <div className="text-xs text-gray-500">{label}</div>
            </div>
        </div>
    </div>
);

// 鏂板/绶ㄨ集鍝侀爡 Modal
const ItemModal = ({ isOpen, onClose, item, onSave, isEdit }) => {
    const [form, setForm] = useState({
        name: '', spec: '', mainCategory: '娑堣€楀搧', category: '榛忚憲鍔?, quantity: 0,
        unit: '鍊?, safeStock: 10, location: '', status: '鍏呰冻'
    });

    useEffect(() => {
        if (item) {
            // 鑻ヨ垔璩囨枡娌掓湁 mainCategory锛屾牴鎿?category 鎺ㄧ畻
            const mainCat = item.mainCategory || getMainCategory(item.category);
            setForm({ ...item, mainCategory: mainCat });
        } else {
            setForm({
                name: '', spec: '', mainCategory: '娑堣€楀搧', category: '榛忚憲鍔?, quantity: 0,
                unit: '鍊?, safeStock: 10, location: '', status: '鍏呰冻'
            });
        }
    }, [item, isOpen]);

    const handleMainCategoryChange = (newMain) => {
        const subCats = CATEGORY_TREE[newMain] || [];
        setForm({
            ...form,
            mainCategory: newMain,
            category: subCats[0] || '' // 闋愯ō閬哥涓€鍊嬪瓙椤炲垾
        });
    };

    const handleSave = () => {
        const status = calculateStatus(parseInt(form.quantity), parseInt(form.safeStock));
        onSave({ ...form, quantity: parseInt(form.quantity), safeStock: parseInt(form.safeStock), status });
    };

    const availableSubCategories = CATEGORY_TREE[form.mainCategory] || [];

    return (
        <Modal
            isOpen={isOpen}
            onClose={onClose}
            title={isEdit ? '绶ㄨ集搴瓨鍝侀爡' : '鏂板搴瓨鍝侀爡'}
            onConfirm={handleSave}
        >
            <div className="space-y-4">
                <InputField
                    label="鍝侀爡鍚嶇ū"
                    value={form.name}
                    onChange={e => setForm({ ...form, name: e.target.value })}
                    placeholder="渚嬶細Panasonic 闁嬮棞"
                />
                <div className="grid grid-cols-2 gap-4">
                    <InputField
                        label="瑕忔牸/鍨嬭櫉"
                        value={form.spec}
                        onChange={e => setForm({ ...form, spec: e.target.value })}
                        placeholder="渚嬶細PN-001"
                    />
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">涓婚鍒?/label>
                        <select
                            value={form.mainCategory}
                            onChange={e => handleMainCategoryChange(e.target.value)}
                            className="w-full px-3 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500"
                        >
                            {Object.keys(CATEGORY_TREE).map(cat => (
                                <option key={cat} value={cat}>{cat}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <div className="grid grid-cols-2 gap-4">
                    <div>
                        <label className="block text-sm font-medium text-gray-700 mb-1">瀛愰鍒?/label>
                        <select
                            value={form.category}
                            onChange={e => setForm({ ...form, category: e.target.value })}
                            className="w-full px-3 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500"
                        >
                            {availableSubCategories.map(cat => (
                                <option key={cat} value={cat}>{cat}</option>
                            ))}
                        </select>
                    </div>
                    <InputField
                        label="鍠綅"
                        value={form.unit}
                        onChange={e => setForm({ ...form, unit: e.target.value })}
                        placeholder="鍊嬨€佺祫銆佺"
                    />
                </div>
                <div className="grid grid-cols-3 gap-4">
                    <InputField
                        label="鏁搁噺"
                        type="number"
                        value={form.quantity}
                        onChange={e => setForm({ ...form, quantity: e.target.value })}
                    />
                    <InputField
                        label="瀹夊叏搴瓨"
                        type="number"
                        value={form.safeStock}
                        onChange={e => setForm({ ...form, safeStock: e.target.value })}
                    />
                    <InputField
                        label="瀛樻斁浣嶇疆"
                        value={form.location}
                        onChange={e => setForm({ ...form, location: e.target.value })}
                        placeholder="A-01"
                    />
                </div>
            </div>
        </Modal>
    );
};


// 鍑哄叆搴?Modal
const StockMovementModal = ({ isOpen, onClose, item, type, onConfirm }) => {
    const [quantity, setQuantity] = useState(1);
    const [note, setNote] = useState('');

    useEffect(() => {
        setQuantity(1);
        setNote('');
    }, [isOpen]);

    const handleConfirm = () => {
        onConfirm({
            itemId: item.id,
            itemName: item.name,
            type: type,
            quantity: parseInt(quantity),
            date: new Date().toISOString().split('T')[0],
            operator: 'Admin',
            note
        });
    };

    return (
        <Modal
            isOpen={isOpen}
            onClose={onClose}
            title={type === '鍏? ? '鍏ュ韩鐧昏' : '鍑哄韩鐧昏'}
            onConfirm={handleConfirm}
        >
            <div className="space-y-4">
                <div className="bg-gray-50 rounded-lg p-3">
                    <div className="text-sm text-gray-500">鍝侀爡</div>
                    <div className="font-bold text-gray-800">{item?.name}</div>
                    <div className="text-xs text-gray-400">{item?.spec}</div>
                    <div className="mt-2 text-sm">
                        鐣跺墠鏁搁噺: <span className="font-bold">{item?.quantity}</span> {item?.unit}
                    </div>
                </div>
                <InputField
                    label={`${type === '鍏? ? '鍏ュ韩' : '鍑哄韩'}鏁搁噺`}
                    type="number"
                    value={quantity}
                    onChange={e => setQuantity(e.target.value)}
                    min={1}
                />
                <InputField
                    label="鍌欒ɑ"
                    value={note}
                    onChange={e => setNote(e.target.value)}
                    placeholder={type === '鍏? ? '渚嬶細鎵归噺鎺¤臣' : '渚嬶細鍑鸿波鑷虫灄鍏え'}
                />
                {type === '鍑? && parseInt(quantity) > (item?.quantity || 0) && (
                    <div className="text-red-500 text-sm flex items-center gap-1">
                        <AlertTriangle size={14} />
                        鍑哄韩鏁搁噺瓒呴亷搴瓨锛乗r
                    </div>
                )}
            </div>
        </Modal>
    );
};

// 鍒櫎纰鸿獚 Modal
const DeleteConfirmModal = ({ isOpen, onClose, item, onConfirm }) => (
    <Modal isOpen={isOpen} onClose={onClose} title="纰鸿獚鍒櫎" onConfirm={onConfirm}>
        <div className="text-center py-4">
            <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
                <Trash2 size={32} className="text-red-500" />
            </div>
            <p className="text-gray-600">纰哄畾瑕佸埅闄や互涓嬪搧闋呭棊锛?/p>
            <p className="font-bold text-gray-800 mt-2">{item?.name}</p>
            <p className="text-sm text-gray-500">{item?.spec}</p>
            <p className="text-sm text-red-500 mt-4">姝ゆ搷浣滅劇娉曢倓鍘?/p>
        </div>
    </Modal>
);

// 涓荤祫浠禱r
const Inventory = ({ data, addToast, onUpdateInventory }) => {
    const [items, setItems] = useState(data || []);
    const [movements, setMovements] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [mainCategoryFilter, setMainCategoryFilter] = useState('鍏ㄩ儴');
    const [subCategoryFilter, setSubCategoryFilter] = useState('鍏ㄩ儴');
    const [statusFilter, setStatusFilter] = useState('鍏ㄩ儴');

    // Modal 鐙€鎱媆r
    const [isAddModalOpen, setIsAddModalOpen] = useState(false);
    const [isEditModalOpen, setIsEditModalOpen] = useState(false);
    const [isDeleteModalOpen, setIsDeleteModalOpen] = useState(false);
    const [isMovementModalOpen, setIsMovementModalOpen] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);
    const [movementType, setMovementType] = useState('鍏?);

    // 搴瓨 Sheet 鐙€鎱媆r
    const [inventorySheet, setInventorySheet] = useState(() => {
        // 寰?localStorage 杓夊叆宸插劜瀛樼殑 Sheet 璩囪▕
        const saved = localStorage.getItem('inventorySheet');
        return saved ? JSON.parse(saved) : null;
    });
    const [isInitializing, setIsInitializing] = useState(false);
    const [isSyncing, setIsSyncing] = useState(false);

    // 鍒濆鍖朶r
    useEffect(() => {
        if (data) setItems(data);
    }, [data]);

    // 鐣?inventorySheet 璁婃洿鏅傚劜瀛樺埌 localStorage
    useEffect(() => {
        if (inventorySheet) {
            localStorage.setItem('inventorySheet', JSON.stringify(inventorySheet));
        }
    }, [inventorySheet]);

    // 绡╅伕閭忚集
    const filteredItems = useMemo(() => {
        return items.filter(item => {
            const matchSearch = !searchTerm ||
                item.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                item.spec?.toLowerCase().includes(searchTerm.toLowerCase());

            // 涓婚鍒ョ閬竆r
            const itemMainCat = item.mainCategory || getMainCategory(item.category);
            const matchMainCategory = mainCategoryFilter === '鍏ㄩ儴' || itemMainCat === mainCategoryFilter;

            // 瀛愰鍒ョ閬竆r
            const matchSubCategory = subCategoryFilter === '鍏ㄩ儴' || item.category === subCategoryFilter;

            const matchStatus = statusFilter === '鍏ㄩ儴' || item.status === statusFilter;
            return matchSearch && matchMainCategory && matchSubCategory && matchStatus;
        });
    }, [items, searchTerm, mainCategoryFilter, subCategoryFilter, statusFilter]);

    // 绲辫▓鏁告摎
    const stats = useMemo(() => {
        const total = items.length;
        const lowStock = items.filter(i => i.status === '搴瓨鍋忎綆').length;
        const outOfStock = items.filter(i => i.status === '缂鸿波').length;
        const thisMonth = movements.filter(m => {
            const moveDate = new Date(m.date);
            const now = new Date();
            return moveDate.getMonth() === now.getMonth() && moveDate.getFullYear() === now.getFullYear();
        });
        const monthIn = thisMonth.filter(m => m.type === '鍏?).reduce((sum, m) => sum + m.quantity, 0);
        const monthOut = thisMonth.filter(m => m.type === '鍑?).reduce((sum, m) => sum + m.quantity, 0);
        return { total, lowStock, outOfStock, monthIn, monthOut };
    }, [items, movements]);

    // 鏂板鍝侀爡
    const handleAddItem = async (newItem) => {
        const itemToAdd = { ...newItem, id: `i-${Date.now()}` };
        const newItems = [...items, itemToAdd];
        setItems(newItems);
        if (onUpdateInventory) onUpdateInventory(newItems);
        await GoogleService.syncToSheet('inventory', newItems);
        addToast('鍝侀爡鏂板鎴愬姛锛?, 'success');
        setIsAddModalOpen(false);
    };

    // 绶ㄨ集鍝侀爡
    const handleEditItem = async (updatedItem) => {
        const newItems = items.map(i => i.id === updatedItem.id ? updatedItem : i);
        setItems(newItems);
        if (onUpdateInventory) onUpdateInventory(newItems);
        await GoogleService.syncToSheet('inventory', newItems);
        addToast('鍝侀爡鏇存柊鎴愬姛锛?, 'success');
        setIsEditModalOpen(false);
        setSelectedItem(null);
    };

    // 鍒櫎鍝侀爡
    const handleDeleteItem = async () => {
        const newItems = items.filter(i => i.id !== selectedItem.id);
        setItems(newItems);
        if (onUpdateInventory) onUpdateInventory(newItems);
        await GoogleService.syncToSheet('inventory', newItems);
        addToast('鍝侀爡宸插埅闄?, 'info');
        setIsDeleteModalOpen(false);
        setSelectedItem(null);
    };

    // 鍑哄叆搴玕r
    const handleStockMovement = async (movement) => {
        // 鏇存柊搴瓨鏁搁噺
        const delta = movement.type === '鍏? ? movement.quantity : -movement.quantity;
        const newItems = items.map(i => {
            if (i.id === movement.itemId) {
                const newQty = Math.max(0, i.quantity + delta);
                return { ...i, quantity: newQty, status: calculateStatus(newQty, i.safeStock) };
            }
            return i;
        });

        // 瑷橀寗鍑哄叆搴玕r
        const newMovement = { ...movement, id: `sm-${Date.now()}` };
        const newMovements = [...movements, newMovement];

        setItems(newItems);
        setMovements(newMovements);
        if (onUpdateInventory) onUpdateInventory(newItems);
        await GoogleService.syncToSheet('inventory', newItems);
        addToast(`${movement.type === '鍏? ? '鍏ュ韩' : '鍑哄韩'}鎴愬姛锛乣, 'success');
        setIsMovementModalOpen(false);
        setSelectedItem(null);
    };

    // 闁嬪暉绶ㄨ集
    const openEdit = (item) => {
        setSelectedItem(item);
        setIsEditModalOpen(true);
    };

    // 闁嬪暉鍒櫎纰鸿獚
    const openDelete = (item) => {
        setSelectedItem(item);
        setIsDeleteModalOpen(true);
    };

    // 闁嬪暉鍑哄叆搴玕r
    const openMovement = (item, type) => {
        setSelectedItem(item);
        setMovementType(type);
        setIsMovementModalOpen(true);
    };

    // 鍒濆鍖栧韩瀛?Sheet
    const initSheet = async () => {
        setIsInitializing(true);
        try {
            const result = await GoogleService.initInventorySheet();
            if (result.success) {
                setInventorySheet({
                    sheetId: result.sheetId,
                    sheetUrl: result.sheetUrl,
                    folderUrl: result.folderUrl
                });
                addToast('搴瓨 Sheet 寤虹珛鎴愬姛锛?, 'success');
            } else {
                addToast('寤虹珛澶辨晽锛? + result.error, 'error');
            }
        } catch (err) {
            addToast('寤虹珛澶辨晽锛? + err.message, 'error');
        }
        setIsInitializing(false);
    };

    // 鍚屾鍒?Sheet
    const syncToSheet = async () => {
        if (!inventorySheet?.sheetId) {
            addToast('璜嬪厛寤虹珛搴瓨 Sheet', 'warning');
            return;
        }
        setIsSyncing(true);
        try {
            const result = await GoogleService.syncInventoryToSheet(inventorySheet.sheetId, items);
            if (result.success) {
                addToast('鍚屾鎴愬姛锛?, 'success');
            } else {
                addToast('鍚屾澶辨晽锛? + result.error, 'error');
            }
        } catch (err) {
            addToast('鍚屾澶辨晽锛? + err.message, 'error');
        }
        setIsSyncing(false);
    };

    // 閲嶇疆 Sheet 閫ｇ祼
    const resetSheet = () => {
        localStorage.removeItem('inventorySheet');
        setInventorySheet(null);
        addToast('宸叉竻闄?Sheet 閫ｇ祼锛岃珛閲嶆柊寤虹珛', 'info');
    };

    return (
        <div className="space-y-6 animate-fade-in">
            <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
                <SectionTitle title="搴瓨绠＄悊" />
                <div className="flex flex-wrap items-center gap-2">
                    {/* Sheet 绠＄悊鎸夐垥 */}
                    {!inventorySheet ? (
                        <button
                            onClick={initSheet}
                            disabled={isInitializing}
                            className="flex items-center gap-2 px-3 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors disabled:opacity-50 text-sm"
                        >
                            {isInitializing ? (
                                <RefreshCw size={16} className="animate-spin" />
                            ) : (
                                <FileSpreadsheet size={16} />
                            )}
                            寤虹珛 Sheet
                        </button>
                    ) : (
                        <>
                            <button
                                onClick={syncToSheet}
                                disabled={isSyncing}
                                className="flex items-center gap-2 px-3 py-2 bg-emerald-600 text-white rounded-lg hover:bg-emerald-700 transition-colors disabled:opacity-50 text-sm"
                            >
                                {isSyncing ? (
                                    <RefreshCw size={16} className="animate-spin" />
                                ) : (
                                    <RefreshCw size={16} />
                                )}
                                鍚屾鍒?Sheet
                            </button>
                            <a
                                href={inventorySheet.sheetUrl}
                                target="_blank"
                                rel="noopener noreferrer"
                                className="flex items-center gap-2 px-3 py-2 bg-blue-100 text-blue-700 rounded-lg hover:bg-blue-200 transition-colors text-sm"
                            >
                                <ExternalLink size={16} />
                                闁嬪暉 Sheet
                            </a>
                            <button
                                onClick={resetSheet}
                                className="flex items-center gap-1 px-2 py-2 text-gray-400 hover:text-red-500 hover:bg-red-50 rounded-lg transition-colors text-sm"
                                title="閲嶇疆 Sheet 閫ｇ祼"
                            >
                                <X size={16} />
                            </button>
                        </>
                    )}
                    <button
                        onClick={() => setIsAddModalOpen(true)}
                        className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition-colors"
                    >
                        <Plus size={18} />
                        鏂板鍝侀爡
                    </button>
                </div>
            </div>

            {/* 绲辫▓鍗＄墖 */}
            <div className="grid grid-cols-2 md:grid-cols-4 gap-3 sm:gap-4">
                <StatCard
                    icon={Box}
                    label="绺藉搧闋呮暩"
                    value={stats.total}
                    color="blue"
                />
                <StatCard
                    icon={AlertTriangle}
                    label="搴瓨鍋忎綆"
                    value={stats.lowStock}
                    color="orange"
                    onClick={() => setStatusFilter('搴瓨鍋忎綆')}
                />
                <StatCard
                    icon={XCircle}
                    label="缂鸿波"
                    value={stats.outOfStock}
                    color="red"
                    onClick={() => setStatusFilter('缂鸿波')}
                />
                <StatCard
                    icon={TrendingDown}
                    label="鏈湀鍑哄韩"
                    value={stats.monthOut}
                    color="purple"
                />
            </div>

            {/* 鎼滃皨鑸囩閬?*/}
            <div className="bg-white rounded-xl p-4 shadow-sm border border-gray-100">
                <div className="flex flex-col md:flex-row gap-4">
                    {/* 鎼滃皨妗?*/}
                    <div className="flex-1 relative">
                        <Search size={18} className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
                        <input
                            type="text"
                            value={searchTerm}
                            onChange={(e) => setSearchTerm(e.target.value)}
                            placeholder="鎼滃皨鍝佸悕鎴栬鏍?.."
                            className="w-full pl-10 pr-4 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                        />
                    </div>

                    {/* 涓婚鍒ョ閬?*/}
                    <select
                        value={mainCategoryFilter}
                        onChange={(e) => {
                            setMainCategoryFilter(e.target.value);
                            setSubCategoryFilter('鍏ㄩ儴'); // 閲嶇疆瀛愰鍒r
                        }}
                        className="px-4 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 bg-white"
                    >
                        {MAIN_CATEGORIES.map(cat => (
                            <option key={cat} value={cat}>{cat === '鍏ㄩ儴' ? '鎵€鏈変富椤炲垾' : cat}</option>
                        ))}
                    </select>

                    {/* 瀛愰鍒ョ閬?*/}
                    <select
                        value={subCategoryFilter}
                        onChange={(e) => setSubCategoryFilter(e.target.value)}
                        className="px-4 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 bg-white"
                    >
                        <option value="鍏ㄩ儴">鎵€鏈夊瓙椤炲垾</option>
                        {getSubCategories(mainCategoryFilter).map(cat => (
                            <option key={cat} value={cat}>{cat}</option>
                        ))}
                    </select>

                    {/* 鐙€鎱嬬閬?*/}
                    <select
                        value={statusFilter}
                        onChange={(e) => setStatusFilter(e.target.value)}
                        className="px-4 py-2 border border-gray-200 rounded-lg focus:ring-2 focus:ring-blue-500 bg-white"
                    >
                        {STATUS_OPTIONS.map(status => (
                            <option key={status} value={status}>{status === '鍏ㄩ儴' ? '鎵€鏈夌媭鎱? : status}</option>
                        ))}
                    </select>

                    {/* 娓呴櫎绡╅伕 */}
                    {(searchTerm || mainCategoryFilter !== '鍏ㄩ儴' || subCategoryFilter !== '鍏ㄩ儴' || statusFilter !== '鍏ㄩ儴') && (
                        <button
                            onClick={() => {
                                setSearchTerm('');
                                setMainCategoryFilter('鍏ㄩ儴');
                                setSubCategoryFilter('鍏ㄩ儴');
                                setStatusFilter('鍏ㄩ儴');
                            }}
                            className="px-4 py-2 text-gray-500 hover:text-gray-700 flex items-center gap-1"
                        >
                            <X size={16} />
                            娓呴櫎
                        </button>
                    )}
                </div>
            </div>

            {/* 搴瓨鍒楄〃 */}
            <div className="bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden">
                <div className="overflow-x-auto">
                    <table className="w-full text-sm">
                        <thead className="bg-gray-50 text-gray-600">
                            <tr>
                                <th className="text-left p-4 font-medium">鍝佸悕</th>
                                <th className="text-left p-4 font-medium">瑕忔牸</th>
                                <th className="text-left p-4 font-medium">椤炲垾</th>
                                <th className="text-center p-4 font-medium">鏁搁噺</th>
                                <th className="text-center p-4 font-medium">瀹夊叏瀛橀噺</th>
                                <th className="text-left p-4 font-medium">浣嶇疆</th>
                                <th className="text-center p-4 font-medium">鐙€鎱?/th>
                                <th className="text-center p-4 font-medium">鎿嶄綔</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredItems.length === 0 ? (
                                <tr>
                                    <td colSpan={8} className="text-center py-12 text-gray-400">
                                        <Package size={48} className="mx-auto mb-2 opacity-50" />
                                        <p>娌掓湁鎵惧埌绗﹀悎姊濅欢鐨勫搧闋?/p>
                                    </td>
                                </tr>
                            ) : (
                                filteredItems.map(item => (
                                    <tr key={item.id} className="border-t border-gray-100 hover:bg-gray-50 transition-colors">
                                        <td className="p-4">
                                            <div className="font-medium text-gray-800">{item.name}</div>
                                        </td>
                                        <td className="p-4 text-gray-500">{item.spec || '-'}</td>
                                        <td className="p-4">
                                            <div className="flex flex-col gap-0.5">
                                                <span className="text-xs text-gray-400">{item.mainCategory || getMainCategory(item.category)}</span>
                                                <span className="px-2 py-1 bg-gray-100 text-gray-600 rounded text-xs inline-block w-fit">
                                                    {item.category || '鍏朵粬'}
                                                </span>
                                            </div>
                                        </td>
                                        <td className="p-4 text-center">
                                            <span className="font-mono font-bold text-gray-800">{item.quantity}</span>
                                            <span className="text-gray-400 ml-1">{item.unit}</span>
                                        </td>
                                        <td className="p-4 text-center text-gray-500">{item.safeStock}</td>
                                        <td className="p-4">
                                            {item.location ? (
                                                <span className="flex items-center gap-1 text-gray-600">
                                                    <MapPin size={14} />
                                                    {item.location}
                                                </span>
                                            ) : '-'}
                                        </td>
                                        <td className="p-4 text-center">
                                            <Badge color={getStatusColor(item.status)}>{item.status}</Badge>
                                        </td>
                                        <td className="p-4">
                                            <div className="flex items-center justify-center gap-1">
                                                <button
                                                    onClick={() => openMovement(item, '鍏?)}
                                                    className="p-1.5 text-green-600 hover:bg-green-50 rounded-lg transition-colors"
                                                    title="鍏ュ韩"
                                                >
                                                    <ArrowDownCircle size={18} />
                                                </button>
                                                <button
                                                    onClick={() => openMovement(item, '鍑?)}
                                                    className="p-1.5 text-purple-600 hover:bg-purple-50 rounded-lg transition-colors"
                                                    title="鍑哄韩"
                                                >
                                                    <ArrowUpCircle size={18} />
                                                </button>
                                                <button
                                                    onClick={() => openEdit(item)}
                                                    className="p-1.5 text-blue-600 hover:bg-blue-50 rounded-lg transition-colors"
                                                    title="绶ㄨ集"
                                                >
                                                    <Edit2 size={18} />
                                                </button>
                                                <button
                                                    onClick={() => openDelete(item)}
                                                    className="p-1.5 text-red-600 hover:bg-red-50 rounded-lg transition-colors"
                                                    title="鍒櫎"
                                                >
                                                    <Trash2 size={18} />
                                                </button>
                                            </div>
                                        </td>
                                    </tr>
                                ))
                            )}
                        </tbody>
                    </table>
                </div>

                {/* 鍒楄〃搴曢儴璩囪▕ */}
                <div className="px-4 py-3 bg-gray-50 border-t border-gray-100 text-sm text-gray-500">
                    椤ず {filteredItems.length} / {items.length} 绛嗚硣鏂橽r
                </div>
            </div>

            {/* 鏈€杩戝嚭鍏ュ韩瑷橀寗 */}
            {movements.length > 0 && (
                <div className="bg-white rounded-xl p-4 shadow-sm border border-gray-100">
                    <h4 className="font-bold text-gray-700 mb-3 flex items-center gap-2">
                        <History size={18} />
                        鏈€杩戝嚭鍏ュ韩瑷橀寗
                    </h4>
                    <div className="space-y-2">
                        {movements.slice(-5).reverse().map(m => (
                            <div key={m.id} className="flex items-center justify-between py-2 border-b border-gray-100 last:border-0">
                                <div className="flex items-center gap-3">
                                    {m.type === '鍏? ? (
                                        <ArrowDownCircle size={18} className="text-green-500" />
                                    ) : (
                                        <ArrowUpCircle size={18} className="text-purple-500" />
                                    )}
                                    <div>
                                        <div className="font-medium text-gray-700">{m.itemName}</div>
                                        <div className="text-xs text-gray-400">{m.note || '-'}</div>
                                    </div>
                                </div>
                                <div className="text-right">
                                    <div className={`font-bold ${m.type === '鍏? ? 'text-green-600' : 'text-purple-600'}`}>
                                        {m.type === '鍏? ? '+' : '-'}{m.quantity}
                                    </div>
                                    <div className="text-xs text-gray-400">{formatDate(m.date)}</div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            {/* Modals */}
            <ItemModal
                isOpen={isAddModalOpen}
                onClose={() => setIsAddModalOpen(false)}
                item={null}
                onSave={handleAddItem}
                isEdit={false}
            />
            <ItemModal
                isOpen={isEditModalOpen}
                onClose={() => { setIsEditModalOpen(false); setSelectedItem(null); }}
                item={selectedItem}
                onSave={handleEditItem}
                isEdit={true}
            />
            <DeleteConfirmModal
                isOpen={isDeleteModalOpen}
                onClose={() => { setIsDeleteModalOpen(false); setSelectedItem(null); }}
                item={selectedItem}
                onConfirm={handleDeleteItem}
            />
            <StockMovementModal
                isOpen={isMovementModalOpen}
                onClose={() => { setIsMovementModalOpen(false); setSelectedItem(null); }}
                item={selectedItem}
                type={movementType}
                onConfirm={handleStockMovement}
            />
        </div>
    );
};

export default Inventory;

