import React, { useState, useEffect } from 'react';
import { FileText, Calculator, Building2, User, Copy, RotateCcw, Check, AlertCircle, Info } from 'lucide-react';
import { SectionTitle } from '../components/common/Indicators';

// 鏁稿瓧杞変腑鏂囧ぇ瀵紙姝ｇ⒑铏曠悊钀€佸剟鍠綅锛塡r
const numberToChinese = (num) => {
    if (num === 0) return '闆?;

    const digits = ['闆?, '澹?, '璨?, '鍙?, '鑲?, '浼?, '闄?, '鏌?, '鎹?, '鐜?];
    const smallUnits = ['', '鎷?, '浣?, '浠?];

    // 灏囨暩瀛楀垎瑙ｇ偤鍎勩€佽惉銆佸€嬩綅鏁哥祫
    const n = Math.floor(num);
    const yi = Math.floor(n / 100000000); // 鍎刓r
    const wan = Math.floor((n % 100000000) / 10000); // 钀琝r
    const ge = n % 10000; // 鍊嬩綅鍒颁粺浣峔r

    // 杞夋彌鍥涗綅鏁哥偤涓枃
    const fourDigitToChinese = (n, addZeroPrefix = false) => {
        if (n === 0) return '';

        let result = '';
        const arr = [
            Math.floor(n / 1000),      // 浠熶綅
            Math.floor((n % 1000) / 100), // 浣颁綅
            Math.floor((n % 100) / 10),   // 鎷句綅
            n % 10                        // 鍊嬩綅
        ];

        let hasValue = false;
        let needZero = addZeroPrefix && n < 1000;

        for (let i = 0; i < 4; i++) {
            if (arr[i] !== 0) {
                if (needZero) {
                    result += '闆?;
                    needZero = false;
                }
                result += digits[arr[i]] + smallUnits[3 - i];
                hasValue = true;
            } else if (hasValue) {
                needZero = true;
            }
        }

        return result;
    };

    let result = '';

    // 铏曠悊鍎刓r
    if (yi > 0) {
        result += fourDigitToChinese(yi) + '鍎?;
    }

    // 铏曠悊钀琝r
    if (wan > 0) {
        result += fourDigitToChinese(wan, yi > 0) + '钀?;
    } else if (yi > 0 && ge > 0) {
        // 鍎勬湁鍊间絾钀矑鏈夛紝鍊嬫湁鍊兼檪闇€瑕佽闆禱r
    }

    // 铏曠悊鍊嬩綅鍒颁粺浣峔r
    if (ge > 0) {
        const needZero = (yi > 0 || wan > 0) && ge < 1000;
        result += fourDigitToChinese(ge, needZero);
    }

    return result || '闆?;
};

// 鏍煎紡鍖栭噾椤嶉’绀篭r
const formatCurrency = (num) => {
    if (!num) return '0';
    return Math.round(num).toLocaleString('zh-TW');
