const PaymentList = ({ onEdit, onBack, addToast }) => {
    const [payments, setPayments] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchTerm, setSearchTerm] = useState('');
    const [statusFilter, setStatusFilter] = useState('ALL');

    const loadPayments = async () => {
        setLoading(true);
        try {
            const data = await PaymentService.getPayments();
            setPayments(data);
        } catch (error) {
            console.error('Load error:', error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        loadPayments();
    }, []);

    // 绡╅伕
    const filteredPayments = useMemo(() => {
        return payments.filter(p => {
            if (statusFilter !== 'ALL' && p.status !== statusFilter) return false;
            if (searchTerm) {
                const term = searchTerm.toLowerCase();
                return (
                    p.paymentNo?.toLowerCase().includes(term) ||
                    p.title?.toLowerCase().includes(term) ||
                    p.projectName?.toLowerCase().includes(term) ||
                    p.customerName?.toLowerCase().includes(term)
                );
            }
            return true;
        });
    }, [payments, statusFilter, searchTerm]);

    // 绲辫▓
    const stats = useMemo(() => {
        const total = payments.length;
        const pending = payments.filter(p => p.status === PAYMENT_STATUS.PENDING).length;
        const invoiced = payments.filter(p => p.status === PAYMENT_STATUS.INVOICED).length;
        const totalRequested = payments.reduce((sum, p) => sum + (p.requestAmount || 0), 0);
        const totalReceived = payments.reduce((sum, p) => sum + (p.receivedAmount || 0), 0);

        return { total, pending, invoiced, totalRequested, totalReceived };
    }, [payments]);

    const handleCreate = () => {
        onEdit(null);
    };

    const handleDelete = async (id) => {
        if (!window.confirm('纰哄畾瑕佸埅闄ゆ璜嬫鍠紵')) return;
        try {
            await PaymentService.deletePayment(id);
            addToast?.('success', '宸插埅闄?);
            loadPayments();
        } catch (error) {
            addToast?.('error', error.message);
        }
    };

    return (
        <div className="space-y-6">
            {/* 妯欓 */}
            <div className="flex items-center justify-between">
                <SectionTitle
                    icon={Receipt}
                    title="璜嬫绠＄悊"
                    subtitle="绠＄悊灏堟璜嬫鑸囨敹娆綷

green" />
            </div>

            {/* 鎼滃皨鑸囩閬?*/}
            <div className="flex gap-3">
                <div className="relative flex-1">
                    <Search size={18} className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
                    <input
                        type="text"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        placeholder="鎼滃皨璜嬫鍠法铏熴€佸皥妗堛€佸鎴?.."
                        className="w-full pl-10 pr-4 py-2.5 border border-gray-200 rounded-xl"
                    />
                </div>
                <select
                    value={statusFilter}
                    onChange={(e) => setStatusFilter(e.target.value)}
                    className="px-4 py-2.5 border border-gray-200 rounded-xl bg-white"
                >
                    <option value="ALL">鍏ㄩ儴鐙€鎱?/option>
                    {Object.entries(PAYMENT_STATUS_LABELS).map(([key, label]) => (
                        <option key={key} value={key}>{label}</option>
                    ))}
                </select>
            </div>

            {/* 璜嬫鍠垪琛?*/}
            <div className="bg-white rounded-xl shadow-sm border border-gray-100 overflow-hidden">
                <table className="w-full">
                    <thead className="bg-gray-50">
                        <tr>
                            <th className="py-3 px-4 text-left text-sm font-medium text-gray-600">璜嬫鍠櫉</th>
                            <th className="py-3 px-4 text-left text-sm font-medium text-gray-600">妯欓</th>
                            <th className="py-3 px-4 text-left text-sm font-medium text-gray-600">灏堟/瀹㈡埗</th>
                            <th className="py-3 px-4 text-center text-sm font-medium text-gray-600">椤炲瀷</th>
                            <th className="py-3 px-4 text-center text-sm font-medium text-gray-600">鐙€鎱?/th>
                            <th className="py-3 px-4 text-right text-sm font-medium text-gray-600">璜嬫閲戦</th>
                            <th className="py-3 px-4 text-right text-sm font-medium text-gray-600">宸叉敹閲戦</th>
                            <th className="py-3 px-4 text-center text-sm font-medium text-gray-600">鍒版湡鏃?/th>
                            <th className="py-3 px-4 text-center text-sm font-medium text-gray-600">鎿嶄綔</th>
                        </tr>
                    </thead>
                    <tbody>
                        {filteredPayments.map(payment => (
                            <tr key={payment.id} className="border-b border-gray-100 hover:bg-gray-50">
                                <td className="py-3 px-4 font-medium">{payment.paymentNo}</td>
                                <td className="py-3 px-4">{payment.title}</td>
                                <td className="py-3 px-4">
