// ============================================
// 鐙€鎱嬪垎浣堝湏椁呭崁
// ============================================
const StatusDistribution = ({ distribution }) => {
    const total = Object.values(distribution || {}).reduce((a, b) => a + b, 0);
    if (total === 0) {
        return (
            <div className="text-center py-4 text-gray-400 text-sm">
                灏氱劇灏堟璩囨枡
            </div>
        );
    }

    const items = [
        { key: 'healthy', label: '鍋ュ悍', color: 'green', count: distribution?.healthy || 0 },
        { key: 'warning', label: '璀︾ず', color: 'yellow', count: distribution?.warning || 0 },
        { key: 'critical', label: '鍗遍毆', color: 'orange', count: distribution?.critical || 0 },
        { key: 'loss', label: '铏ф悕', color: 'red', count: distribution?.loss || 0 },
    ];

    return (
        <div className="space-y-3">
            {items.map(item => (
                <div key={item.key} className="flex items-center gap-3">
                    <div className={`w-3 h-3 rounded-full bg-${item.color}-500`} />
                    <span className="flex-1 text-sm text-gray-600">{item.label}</span>
                    <span className="font-semibold">{item.count}</span>
                    <span className="text-sm text-gray-400">
                        ({total > 0 ? ((item.count / total) * 100).toFixed(0) : 0}%)
                    </span>
                </div>
            ))}
        </div>
    );
};

// ============================================
// 鏈堝害瓒ㄥ嫝鍦栬〃
// ============================================
const MonthlyTrendChart = ({ data }) => {
    if (!data?.length) return null;

    const maxValue = Math.max(...data.map(d => Math.max(d.revenue, d.cost)));

    return (
        <div className="space-y-4">
            <div className="flex justify-end gap-4 text-sm">
                <div className="flex items-center gap-2">
                    <div className="w-3 h-3 rounded-full bg-blue-500" />
                    <span className="text-gray-600">鏀跺叆</span>
                </div>
                <div className="flex items-center gap-2">
                    <div className="w-3 h-3 rounded-full bg-red-400" />
                    <span className="text-gray-600">鎴愭湰</span>
                </div>
            </div>
            <div className="flex items-end justify-between gap-2 h-40">
                {data.map((item, i) => (
                    <div key={i} className="flex-1 flex flex-col items-center gap-1">
                        <div className="flex-1 w-full flex items-end justify-center gap-1">
                            <div
                                className="w-3 bg-blue-500 rounded-t"
                                style={{ height: `${(item.revenue / maxValue) * 100}%` }}
                                title={`鏀跺叆: ${formatFullCurrency(item.revenue)}`}
                            />
                            <div
                                className="w-3 bg-red-400 rounded-t"
                                style={{ height: `${(item.cost / maxValue) * 100}%` }}
                                title={`鎴愭湰: ${formatFullCurrency(item.cost)}`}
                            />
                        </div>
                        <span className="text-xs text-gray-500">{item.month}</span>
                    </div>
                ))}
            </div>
        </div>
    );
};

