const ProfitAnalysis = ({ addToast }) => {
    const [metrics, setMetrics] = useState(null);
    const [alerts, setAlerts] = useState([]);
    const [loading, setLoading] = useState(true);

    const loadData = async () => {
        setLoading(true);
        try {
            const [metricsData, alertsData] = await Promise.all([
                ProfitAnalysisService.getDashboardMetrics(),
                ProfitAnalysisService.getAlerts(),
            ]);
            setMetrics(metricsData);
            setAlerts(alertsData);
        } catch (error) {
            console.error('Load error:', error);
            addToast?.('error', '杓夊叆璩囨枡澶辨晽');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        loadData();
    }, []);

    if (loading) {
        return (
            <div className="flex items-center justify-center h-64">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-orange-500" />
            </div>
        );
    }

    return (
        <div className="space-y-6">
            {/* 妯欓 */}
            <SectionTitle
                icon={BarChart3}
                title="鍒╂饯鍒嗘瀽"
                subtitle="灏堟鎴愭湰銆佹敹鍏ヨ垏姣涘埄缍滃悎鍒嗘瀽"
            />

            {/* 涓昏鎸囨 */}
            <div className="grid grid-cols-4 gap-4">
                <BigStatCard
                    icon={DollarSign}
                    label="绺芥敹鍏

red"
                />
                <BigStatCard
                    icon={TrendingUp}
                    label="姣涘埄"
                    value={formatCurrency(metrics?.totalProfit || 0)}
                    subValue={`姣涘埄鐜?${metrics?.overallMargin || 0}%`}
                    color="green"
                />
                <BigStatCard
                    icon={Percent}
                    label="鏀舵鐜嘰

orange"
                />
            </div>

            {/* 涓枔鍗€濉?*/}
            <div className="grid grid-cols-3 gap-6">
                {/* 灏堟鐙€鎱嬪垎浣?*/}
                <div className="bg-white rounded-2xl p-6 border border-gray-100 shadow-sm">
                    <h3 className="font-semibold text-gray-800 mb-4 flex items-center gap-2">
                        <PieChart size={18} /> 灏堟姣涘埄鐙€鎱媆r
                    </h3>
                    <StatusDistribution distribution={metrics?.statusDistribution} />
                </div>

                {/* 璀﹀牨 */}
                <div className="bg-white rounded-2xl p-6 border border-gray-100 shadow-sm">
                    <h3 className="font-semibold text-gray-800 mb-4 flex items-center gap-2">
                        <AlertTriangle size={18} /> 璀﹀牨閫氱煡
                        {alerts.length > 0 && (
                            <span className="px-2 py-0.5 bg-red-100 text-red-600 text-xs rounded-full">
                                {alerts.length}
                            </span>
                        )}
                    </h3>
                    <AlertList alerts={alerts} />
                </div>

                {/* 鏈堝害瓒ㄥ嫝 */}
                <div className="bg-white rounded-2xl p-6 border border-gray-100 shadow-sm">
                    <h3 className="font-semibold text-gray-800 mb-4 flex items-center gap-2">
