const Quotations = ({ addToast, projects = [], clients = [] }) => {
    const [quotations, setQuotations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [showNewModal, setShowNewModal] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [statusFilter, setStatusFilter] = useState('ALL');
    const [selectedQuotation, setSelectedQuotation] = useState(null);
    const [viewMode, setViewMode] = useState('list'); // list / editor

    // 杓夊叆浼板児鍠甛r
    useEffect(() => {
        loadQuotations();
    }, []);

    const loadQuotations = async () => {
        setLoading(true);
        try {
            const data = await QuotationService.getQuotations();
            setQuotations(data);
        } catch (error) {
            console.error('Failed to load quotations:', error);
        } finally {
            setLoading(false);
        }
    };

    // 绡╅伕浼板児鍠甛r
    const filteredQuotations = useMemo(() => {
        return quotations.filter(q => {
            // 鐙€鎱嬬閬竆r
            if (statusFilter !== 'ALL' && q.status !== statusFilter) return false;
            // 鎼滃皨
            if (searchTerm) {
                const term = searchTerm.toLowerCase();
                return (
                    q.quotationNo?.toLowerCase().includes(term) ||
                    q.title?.toLowerCase().includes(term) ||
                    q.customerName?.toLowerCase().includes(term) ||
                    q.projectName?.toLowerCase().includes(term)
                );
            }
            return true;
        });
    }, [quotations, statusFilter, searchTerm]);

    // 绲辫▓
    const stats = useMemo(() => {
        const total = quotations.length;
        const draft = quotations.filter(q => q.status === QUOTATION_STATUS.DRAFT).length;
        const pending = quotations.filter(q => q.status === QUOTATION_STATUS.PENDING).length;
        const approved = quotations.filter(q => q.status === QUOTATION_STATUS.APPROVED).length;
        const totalAmount = quotations
            .filter(q => [QUOTATION_STATUS.APPROVED, QUOTATION_STATUS.SENT, QUOTATION_STATUS.ACCEPTED].includes(q.status))
            .reduce((sum, q) => sum + (q.totalAmount || 0), 0);

        return { total, draft, pending, approved, totalAmount };
    }, [quotations]);

    // 鏂板浼板児鍠甛r
    const handleCreate = async (data) => {
        try {
            const newQuotation = await QuotationService.createQuotation(data);
            loadQuotations();
            // 鑷嫊閫插叆绶ㄨ集妯″紡
            setSelectedQuotation(newQuotation);
            setViewMode('editor');
        } catch (error) {
            console.error('Failed to create quotation:', error);
        }
    };

    // 瑜囪＝浼板児鍠甛r
    const handleCopy = async (quotation) => {
        if (window.confirm(`纰哄畾瑕佽瑁戒及鍍瑰柈銆?{quotation.title}銆嶅棊锛焋)) {
            try {
                await QuotationService.copyQuotation(quotation.id);
                loadQuotations();
            } catch (error) {
                console.error('Failed to copy quotation:', error);
            }
        }
    };

    // 鍒櫎浼板児鍠甛r
    const handleDelete = async (quotation) => {
        if (window.confirm(`纰哄畾瑕佸埅闄や及鍍瑰柈銆?{quotation.title}銆嶅棊锛熸鎿嶄綔鐒℃硶寰╁師銆俙)) {
            try {
                await QuotationService.deleteQuotation(quotation.id);
                loadQuotations();
            } catch (error) {
                console.error('Failed to delete quotation:', error);
            }
        }
    };

    // 绶ㄨ集浼板児鍠甛r
    const handleEdit = (quotation) => {
        setSelectedQuotation(quotation);
        setViewMode('editor');
    };

    // 妾㈣浼板児鍠甛r
    const handleView = (quotation) => {
        setSelectedQuotation(quotation);
        setViewMode('editor');
    };

    // 杩斿洖鍒楄〃
    const handleBack = () => {
        setSelectedQuotation(null);
        setViewMode('list');
        loadQuotations(); // 閲嶆柊杓夊叆
    };

    const formatCurrency = (amount) => {
        return new Intl.NumberFormat('zh-TW', {
            style: 'currency',
            currency: 'TWD',
            minimumFractionDigits: 0,
        }).format(amount || 0);
    };

    // 绶ㄨ集妯″紡
    if (viewMode === 'editor' && selectedQuotation) {
        return (
            <QuotationEditor
                quotationId={selectedQuotation.id}
                onBack={handleBack}
                addToast={addToast}
            />
        );
    }

    return (
        <div className="space-y-6">
            {/* 妯欓鑸囨搷浣?*/}
            <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
                <SectionTitle
                    icon={FileText}
                    title="浼板児鍠鐞哱

px-4 py-2 bg-orange-500 text-white rounded-xl hover:bg-orange-600 transition-colors flex items-center gap-2 shadow-md"
                >
                    <Plus size={18} />
                    鏂板浼板児鍠甛r
                </button>
            </div>

            {/* 绲辫▓鍗＄墖 */}
            <div className="grid grid-cols-2 lg:grid-cols-5 gap-4">
                <div className="bg-white rounded-xl p-4 border border-gray-100">
                    <div className="text-sm text-gray-500 mb-1">鍏ㄩ儴浼板児鍠?/div>
                    <div className="text-2xl font-bold text-gray-800">{stats.total}</div>
                </div>
                <div className="bg-white rounded-xl p-4 border border-gray-100">
                    <div className="text-sm text-gray-500 mb-1">鑽夌涓?/div>
                    <div className="text-2xl font-bold text-gray-600">{stats.draft}</div>
                </div>
                <div className="bg-white rounded-xl p-4 border border-gray-100">
                    <div className="text-sm text-gray-500 mb-1">寰呭鏍?/div>
                    <div className="text-2xl font-bold text-yellow-600">{stats.pending}</div>
                </div>
                <div className="bg-white rounded-xl p-4 border border-gray-100">
                    <div className="text-sm text-gray-500 mb-1">宸叉牳鍑?/div>
                    <div className="text-2xl font-bold text-green-600">{stats.approved}</div>
                </div>
                <div className="bg-white rounded-xl p-4 border border-gray-100 col-span-2 lg:col-span-1">
                    <div className="text-sm text-gray-500 mb-1">宸叉牳鍑嗛噾椤?/div>
                    <div className="text-xl font-bold text-orange-600">{formatCurrency(stats.totalAmount)}</div>
                </div>
            </div>

            {/* 鎼滃皨鑸囩閬?*/}
            <div className="flex flex-col sm:flex-row gap-3">
                <div className="relative flex-1">
                    <Search size={18} className="absolute left-3 top-1/2 -translate-y-1/2 text-gray-400" />
                    <input
                        type="text"
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        placeholder="鎼滃皨浼板児鍠法铏熴€佹椤屻€佸鎴?.."
                        className="w-full pl-10 pr-4 py-2.5 border border-gray-200 rounded-xl focus:ring-2 focus:ring-orange-500 focus:border-transparent"
                    />
                </div>
                <select
                    value={statusFilter}
                    onChange={(e) => setStatusFilter(e.target.value)}
                    className="px-4 py-2.5 border border-gray-200 rounded-xl focus:ring-2 focus:ring-orange-500 focus:border-transparent bg-white min-w-[140px]"
                >
                    <option value="ALL">鍏ㄩ儴鐙€鎱?/option>
                    {Object.entries(QUOTATION_STATUS_LABELS).map(([key, label]) => (
                        <option key={key} value={key}>{label}</option>
                    ))}
                </select>
            </div>

            {/* 浼板児鍠垪琛?*/}
            {loading ? (
                <div className="text-center py-12 text-gray-500">
                    杓夊叆涓?..
                </div>
            ) : filteredQuotations.length === 0 ? (
                <div className="text-center py-12">
                    <FileText size={48} className="mx-auto text-gray-300 mb-4" />
                    <h3 className="text-lg font-medium text-gray-600 mb-2">
                        {searchTerm || statusFilter !== 'ALL' ? '娌掓湁绗﹀悎姊濅欢鐨勪及鍍瑰柈' : '灏氱劇浼板児鍠?}
                    </h3>
                    <p className="text-gray-500 mb-4">
                        {searchTerm || statusFilter !== 'ALL'
                            ? '璜嬭鏁存悳灏嬫浠舵垨绡╅伕姊濅欢'
                            : '榛炴搳涓婃柟鎸夐垥寤虹珛鎮ㄧ殑绗竴寮典及鍍瑰柈'}
                    </p>
                    {!searchTerm && statusFilter === 'ALL' && (
                        <button
                            onClick={() => setShowNewModal(true)}
                            className="px-4 py-2 bg-orange-500 text-white rounded-lg hover:bg-orange-600 transition-colors"
                        >
                            寤虹珛浼板児鍠甛r
                        </button>
                    )}
                </div>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {filteredQuotations.map(quotation => (
                        <QuotationCard
                            key={quotation.id}
                            quotation={quotation}
                            onView={handleView}
                            onEdit={handleEdit}
                            onCopy={handleCopy}
                            onDelete={handleDelete}
                        />
                    ))}
                </div>
            )}

            {/* 鏂板 Modal */}
            <NewQuotationModal
                isOpen={showNewModal}
                onClose={() => setShowNewModal(false)}
                onSubmit={handleCreate}
                projects={projects}
                customers={clients}
            />
        </div>
    );
};

