    { id: 'cost', label: '鎴愭湰浼扮畻', icon: '馃М' },
    { id: 'calc', label: '鐗╂枡鎻涚畻', icon: '馃彚' },
];

// Role level labels
const ROLE_LABELS = {
    super_admin: { label: '鏈€楂樼鐞嗗摗', color: 'bg-purple-500', textColor: 'text-purple-600' },
    admin: { label: '绠＄悊鍝?, color: 'bg-blue-500', textColor: 'text-blue-600' },
    user: { label: '涓€鑸娇鐢ㄨ€?, color: 'bg-gray-400', textColor: 'text-gray-600' },
};

const UserManagement = ({ addToast }) => {
    const { user: currentUser } = useAuth();
    const [users, setUsers] = useState([]);
    const [roles, setRoles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [searchQuery, setSearchQuery] = useState('');
    const [activeTab, setActiveTab] = useState('users'); // 'users' | 'roles'
    const [editingRole, setEditingRole] = useState(null);
    const [rolePermissions, setRolePermissions] = useState({});

    // Load users and roles
    useEffect(() => {
        loadData();
    }, []);

    const loadData = async () => {
        try {
            setLoading(true);
            const [usersData, rolesData] = await Promise.all([
                getAllUsers(),
                getAllRoles(),
            ]);
            setUsers(usersData);
            setRoles(rolesData);

            // Initialize role permissions
            const permissions = {};
            rolesData.forEach(role => {
                permissions[role.name] = role.allowedPages || [];
            });
            setRolePermissions(permissions);
        } catch (error) {
            console.error('Error loading data:', error);
            addToast?.('杓夊叆璩囨枡澶辨晽', 'error');
        } finally {
            setLoading(false);
        }
    };

    // Handle role change for a user
    const handleRoleChange = async (userId, newRole) => {
        if (userId === currentUser?.uid) {
            addToast?.('鐒℃硶鏇存敼鑷繁鐨勮鑹?, 'warning');
            return;
        }

        try {
            await updateUserRole(userId, newRole);
            setUsers(prev => prev.map(u =>
                u.uid === userId ? { ...u, role: newRole } : u
            ));
            addToast?.('瑙掕壊宸叉洿鏂?, 'success');
        } catch (error) {
            console.error('Error updating role:', error);
            addToast?.('鏇存柊瑙掕壊澶辨晽', 'error');
        }
    };

    // Handle delete user
    const handleDeleteUser = async (userId) => {
        if (userId === currentUser?.uid) {
            addToast?.('鐒℃硶鍒櫎鑷繁鐨勫赋铏?, 'warning');
            return;
        }

        if (!window.confirm('纰哄畾瑕佸埅闄ゆ浣跨敤鑰呭棊锛?)) return;

        try {
            await deleteUser(userId);
            setUsers(prev => prev.filter(u => u.uid !== userId));
            addToast?.('浣跨敤鑰呭凡鍒櫎', 'success');
        } catch (error) {
            console.error('Error deleting user:', error);
            addToast?.('鍒櫎浣跨敤鑰呭け鏁?, 'error');
        }
    };

    // Toggle page permission for a role
    const togglePagePermission = (roleName, pageId) => {
        if (roleName === 'super_admin') {
            addToast?.('鐒℃硶淇敼鏈€楂樼鐞嗗摗娆婇檺', 'warning');
            return;
        }

        setRolePermissions(prev => {
            const current = prev[roleName] || [];
            const updated = current.includes(pageId)
                ? current.filter(p => p !== pageId)
                : [...current, pageId];
            return { ...prev, [roleName]: updated };
        });
    };

    // Save role permissions
    const saveRolePermissions = async (roleName) => {
        try {
            const roleConfig = roles.find(r => r.name === roleName);
            await updateRoleConfig(roleName, {
                ...roleConfig,
                allowedPages: rolePermissions[roleName],
            });
            addToast?.('娆婇檺宸插劜瀛?, 'success');
            setEditingRole(null);
            loadData();
        } catch (error) {
            console.error('Error saving permissions:', error);
            addToast?.('鍎插瓨娆婇檺澶辨晽', 'error');
        }
    };

